import { useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
    // Outros clientes...
  ]);

  const [produtos, setProdutos] = useState([
    { id: 1, nome: "Produto A", descricao: "Descrição do Produto A", preco: 20.0 },
    { id: 2, nome: "Produto B", descricao: "Descrição do Produto B", preco: 35.0 },
    // Outros produtos...
  ]);

  const [servicos, setServicos] = useState([
    { id: 1, nome: "Serviço A", descricao: "Descrição do Serviço A", preco: 100.0 },
    { id: 2, nome: "Serviço B", descricao: "Descrição do Serviço B", preco: 150.0 },
    // Outros serviços...
  ]);

  const [isRegistering, setIsRegistering] = useState(false);
  const [newCliente, setNewCliente] = useState({ nome: "", cpf: "", telefone: "", valorConsumido: 0 });
  const [newProduto, setNewProduto] = useState({ nome: "", descricao: "", preco: 0 });
  const [newServico, setNewServico] = useState({ nome: "", descricao: "", preco: 0 });

  const handleRegisterCliente = () => {
    if (newCliente.nome && newCliente.cpf && newCliente.telefone) {
      setClientes([
        ...clientes,
        { ...newCliente, id: clientes.length + 1 }
      ]);
      setIsRegistering(false);
      setNewCliente({ nome: "", cpf: "", telefone: "", valorConsumido: 0 });
    } else {
      alert("Preencha todos os campos!");
    }
  };

  const handleRegisterProduto = () => {
    if (newProduto.nome && newProduto.descricao) {
      setProdutos([
        ...produtos,
        { ...newProduto, id: produtos.length + 1 }
      ]);
      setIsRegistering(false);
      setNewProduto({ nome: "", descricao: "", preco: 0 });
    } else {
      alert("Preencha todos os campos!");
    }
  };

  const handleRegisterServico = () => {
    if (newServico.nome && newServico.descricao) {
      setServicos([
        ...servicos,
        { ...newServico, id: servicos.length + 1 }
      ]);
      setIsRegistering(false);
      setNewServico({ nome: "", descricao: "", preco: 0 });
    } else {
      alert("Preencha todos os campos!");
    }
  };

  const handleDelete = (type: string, id: number) => {
    if (type === "cliente") {
      setClientes(clientes.filter(cliente => cliente.id !== id));
    } else if (type === "produto") {
      setProdutos(produtos.filter(produto => produto.id !== id));
    } else if (type === "servico") {
      setServicos(servicos.filter(servico => servico.id !== id));
    }
  };

  const handleEdit = (type: string, id: number) => {
    if (type === "cliente") {
      const updatedClientes = clientes.map(cliente => cliente.id === id ? { ...cliente, isEditing: true } : cliente);
      setClientes(updatedClientes);
    } else if (type === "produto") {
      const updatedProdutos = produtos.map(produto => produto.id === id ? { ...produto, isEditing: true } : produto);
      setProdutos(updatedProdutos);
    } else if (type === "servico") {
      const updatedServicos = servicos.map(servico => servico.id === id ? { ...servico, isEditing: true } : servico);
      setServicos(updatedServicos);
    }
  };

  const handleSave = (type: string, id: number, updatedData: any) => {
    if (type === "cliente") {
      const updatedClientes = clientes.map(cliente => cliente.id === id ? { ...cliente, ...updatedData, isEditing: false } : cliente);
      setClientes(updatedClientes);
    } else if (type === "produto") {
      const updatedProdutos = produtos.map(produto => produto.id === id ? { ...produto, ...updatedData, isEditing: false } : produto);
      setProdutos(updatedProdutos);
    } else if (type === "servico") {
      const updatedServicos = servicos.map(servico => servico.id === id ? { ...servico, ...updatedData, isEditing: false } : servico);
      setServicos(updatedServicos);
    }
  };

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Clientes</a>
          <a style={botaoStyle} href="/listaProduto">Produtos</a>
          <a style={botaoStyle} href="/listaServico">Serviços</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <h5>Listagens de Clientes</h5>
        {/* Botão para registrar novo cliente */}
        <button style={{ backgroundColor: "blue", color: "white", marginBottom: "20px" }} onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Cancelar Registro" : "Registrar Novo Cliente"}
        </button>
        {/* Formulário de Registro Cliente */}
        {isRegistering && (
          <div>
            <input type="text" placeholder="Nome" value={newCliente.nome} onChange={(e) => setNewCliente({ ...newCliente, nome: e.target.value })} />
            <input type="text" placeholder="CPF" value={newCliente.cpf} onChange={(e) => setNewCliente({ ...newCliente, cpf: e.target.value })} />
            <input type="text" placeholder="Telefone" value={newCliente.telefone} onChange={(e) => setNewCliente({ ...newCliente, telefone: e.target.value })} />
            <input type="number" placeholder="Valor Consumido" value={newCliente.valorConsumido} onChange={(e) => setNewCliente({ ...newCliente, valorConsumido: parseFloat(e.target.value) })} />
            <button style={{ backgroundColor: "green", color: "white", marginTop: "10px" }} onClick={handleRegisterCliente}>Registrar</button>
          </div>
        )}

        {/* Listagem de Clientes */}
        <table className="striped">
          <thead>
            <tr>
              <th>Nome</th>
              <th>CPF</th>
              <th>Telefone</th>
              <th>Valor Consumido</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map(cliente => (
              <tr key={cliente.id}>
                <td>{cliente.nome}</td>
                <td>{cliente.cpf}</td>
                <td>{cliente.telefone}</td>
                <td>{cliente.valorConsumido}</td>
                <td>
                  <button style={{ backgroundColor: "yellow", color: "black" }} onClick={() => handleEdit("cliente", cliente.id)}>Editar</button>
                  <button style={{ backgroundColor: "red", color: "white", marginLeft: "5px" }} onClick={() => handleDelete("cliente", cliente.id)}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Listagem de Produtos */}
        <h5>Listagens de Produtos</h5>
        <button style={{ backgroundColor: "blue", color: "white", marginBottom: "20px" }} onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Cancelar Registro" : "Registrar Novo Produto"}
        </button>
        {isRegistering && (
          <div>
            <input type="text" placeholder="Nome" value={newProduto.nome} onChange={(e) => setNewProduto({ ...newProduto, nome: e.target.value })} />
            <input type="text" placeholder="Descrição" value={newProduto.descricao} onChange={(e) => setNewProduto({ ...newProduto, descricao: e.target.value })} />
            <input type="number" placeholder="Preço" value={newProduto.preco} onChange={(e) => setNewProduto({ ...newProduto, preco: parseFloat(e.target.value) })} />
            <button style={{ backgroundColor: "green", color: "white", marginTop: "10px" }} onClick={handleRegisterProduto}>Registrar</button>
          </div>
        )}
        <table className="striped">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Preço</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.map(produto => (
              <tr key={produto.id}>
                <td>{produto.nome}</td>
                <td>{produto.descricao}</td>
                <td>{produto.preco}</td>
                <td>
                  <button style={{ backgroundColor: "yellow", color: "black" }} onClick={() => handleEdit("produto", produto.id)}>Editar</button>
                  <button style={{ backgroundColor: "red", color: "white", marginLeft: "5px" }} onClick={() => handleDelete("produto", produto.id)}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Listagem de Serviços */}
        <h5>Listagens de Serviços</h5>
        <button style={{ backgroundColor: "blue", color: "white", marginBottom: "20px" }} onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Cancelar Registro" : "Registrar Novo Serviço"}
        </button>
        {isRegistering && (
          <div>
            <input type="text" placeholder="Nome" value={newServico.nome} onChange={(e) => setNewServico({ ...newServico, nome: e.target.value })} />
            <input type="text" placeholder="Descrição" value={newServico.descricao} onChange={(e) => setNewServico({ ...newServico, descricao: e.target.value })} />
            <input type="number" placeholder="Preço" value={newServico.preco} onChange={(e) => setNewServico({ ...newServico, preco: parseFloat(e.target.value) })} />
            <button style={{ backgroundColor: "green", color: "white", marginTop: "10px" }} onClick={handleRegisterServico}>Registrar</button>
          </div>
        )}
        <table className="striped">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Descrição</th>
              <th>Preço</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {servicos.map(servico => (
              <tr key={servico.id}>
                <td>{servico.nome}</td>
                <td>{servico.descricao}</td>
                <td>{servico.preco}</td>
                <td>
                  <button style={{ backgroundColor: "yellow", color: "black" }} onClick={() => handleEdit("servico", servico.id)}>Editar</button>
                  <button style={{ backgroundColor: "red", color: "white", marginLeft: "5px" }} onClick={() => handleDelete("servico", servico.id)}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Listagens;
