import { useState, useEffect } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const FormularioCadastroServico = () => {
  const [nomeServico, setNomeServico] = useState("");
  const [valorServico, setValorServico] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newService = { nome: nomeServico, valor: valorServico };

    // Salva o novo serviço no localStorage
    const existingServices = JSON.parse(localStorage.getItem("servicos") || "[]");
    localStorage.setItem("servicos", JSON.stringify([...existingServices, newService]));

    setNomeServico("");
    setValorServico("");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Lista de Serviços
          </a>
        </div>
      </nav>
      <br />
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="text"
              placeholder="Nome do Serviço"
              value={nomeServico}
              onChange={(e) => setNomeServico(e.target.value)}
            />
          </div>
          <div>
            <input
              type="text"
              placeholder="Valor do Serviço"
              value={valorServico}
              onChange={(e) => setValorServico(e.target.value)}
            />
          </div>
          <div>
            <input
              type="text"
              placeholder="Descrição do Serviço"
              value={valorServico}
              onChange={(e) => setDescricaoServico(e.target.value)}
            />
          </div>
          <button style={botaoStyle} type="submit">
            Registrar
          </button>
        </form>
      </div>
    </div>
  );
};

export default FormularioCadastroServico;
