import React, { useState } from "react";
import CSS from "csstype";

type Consumo = {
  id: string;
  cliente: string;
  servico: string;
  produto: string;
  precoServico: number;
  dataCadastro: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const CadastroConsumo = () => {
  const [cliente, setCliente] = useState("");
  const [servico, setServico] = useState("");
  const [produto, setProduto] = useState("");
  const [precoServico, setPrecoServico] = useState<number>(0);
  const [dataCadastro, setDataCadastro] = useState("");

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name === "cliente") setCliente(value);
    if (name === "servico") setServico(value);
    if (name === "produto") setProduto(value);
    if (name === "precoServico") setPrecoServico(parseFloat(value));
    if (name === "dataCadastro") setDataCadastro(value);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const consumoNovo: Consumo = {
      id: String(Date.now()),
      cliente,
      servico,
      produto,
      precoServico,
      dataCadastro,
    };

    const consumos = JSON.parse(localStorage.getItem("consumos") || "[]");
    consumos.push(consumoNovo);
    localStorage.setItem("consumos", JSON.stringify(consumos));

    setCliente("");
    setServico("");
    setProduto("");
    setPrecoServico(0);
    setDataCadastro("");

    alert("Consumo cadastrado com sucesso!");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">
            Cadastros
          </a>
          <a style={botaoStyle} href="/listaCliente">
            Cliente
          </a>
          <a style={botaoStyle} href="/listaProduto">
            Produto
          </a>
          <a style={botaoStyle} href="/listaPedidos">
            Consumo
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Serviços
          </a>
          <a style={botaoStyle} href="/Listagens">
            Listagens
          </a>
        </div>
      </nav>

      <div className="container">
        <h5 className="center-align">Cadastro de Consumo</h5>
        <form onSubmit={handleSubmit}>
          <div className="input-field">
            <label>Cliente:</label>
            <select
              name="cliente"
              value={cliente}
              onChange={handleChange}
              required
            >
              <option value="">Selecione o Cliente</option>
              <option value="João Silva">João Silva</option>
              <option value="Maria Souza">Maria Souza</option>
              <option value="Ana Oliveira">Ana Oliveira</option>
              <option value="Carlos Pereira">Carlos Pereira</option>
              <option value="Camila Santos">Camila Santos</option>
              <option value="Paulo Costa">Paulo Costa</option>
              <option value="Lara Fernandes">Lara Fernandes</option>
              <option value="Juliana Lima">Juliana Lima</option>
              <option value="Fábio Rocha">Fábio Rocha</option>
              <option value="Roberta Alves">Roberta Alves</option>
            </select>
          </div>

          <div className="input-field">
            <label>Serviço:</label>
            <select
              name="servico"
              value={servico}
              onChange={handleChange}
              required
            >
              <option value="">Selecione o Serviço</option>
              <option value="Limpeza de Pele">Limpeza de Pele</option>
              <option value="Hidratação Facial">Hidratação Facial</option>
              <option value="Maquiagem Profissional">Maquiagem Profissional</option>
              <option value="Depilação a Cera">Depilação a Cera</option>
              <option value="Massagem Relaxante">Massagem Relaxante</option>
              <option value="Penteado para Festa">Penteado para Festa</option>
              <option value="Tratamento Capilar">Tratamento Capilar</option>
              <option value="Manicure e Pedicure">Manicure e Pedicure</option>
              <option value="Design de Sobrancelhas">Design de Sobrancelhas</option>
              <option value="Cuidados com a Pele">Cuidados com a Pele</option>
            </select>
          </div>

          <div className="input-field">
            <label>Produto:</label>
            <select
              name="produto"
              value={produto}
              onChange={handleChange}
              required
            >
              <option value="">Selecione o Produto</option>
              <option value="Shampoo Hidratante">Shampoo Hidratante</option>
              <option value="Creme de Hidratação Intensiva">Creme de Hidratação Intensiva</option>
              <option value="Máscara de Tratamento Capilar">Máscara de Tratamento Capilar</option>
              <option value="Esfoliante Corporal">Esfoliante Corporal</option>
              <option value="Base Líquida">Base Líquida</option>
              <option value="Creme Anti-idade">Creme Anti-idade</option>
              <option value="Perfume Floral">Perfume Floral</option>
              <option value="Protetor Solar Facial">Protetor Solar Facial</option>
              <option value="Kit Manicure">Kit Manicure</option>
              <option value="Óleo Essencial">Óleo Essencial</option>
            </select>
          </div>

          <div className="input-field">
            <label>Preço do Serviço (R$):</label>
            <input
              type="number"
              name="precoServico"
              value={precoServico}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-field">
            <label>Data Cadastro:</label>
            <input
              type="date"
              name="dataCadastro"
              value={dataCadastro}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" style={botaoStyle}>
            Registrar Consumo
          </button>
        </form>
      </div>
    </div>
  );
};

export default CadastroConsumo;
