import { useState, useEffect } from "react";
import CSS from "csstype";
import { Link } from "react-router-dom";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

export default function ListaServicos() {
  const [servicos, setServicos] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [editNome, setEditNome] = useState("");
  const [editValor, setEditValor] = useState("");
  const [editDescricao, setEditDescricao] = useState("");

  useEffect(() => {
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    setServicos(servicos);
  }, []);

  const handleDelete = (index) => {
    const updatedServicos = [...servicos];
    updatedServicos.splice(index, 1);
    setServicos(updatedServicos);
    localStorage.setItem("servicos", JSON.stringify(updatedServicos));
  };

  const handleEdit = (index) => {
    setEditIndex(index);
    setEditNome(servicos[index].nome);
    setEditValor(servicos[index].valor);
    setEditDescricao(servicos[index].descricao);
  };

  const handleSave = () => {
    const updatedServicos = [...servicos];
    updatedServicos[editIndex] = { nome: editNome, valor: editValor, descricao: editDescricao };
    setServicos(updatedServicos);
    localStorage.setItem("servicos", JSON.stringify(updatedServicos));
    setEditIndex(null);
    setEditNome("");
    setEditValor("");
    setEditDescricao("");
  };

  const handleCancel = () => {
    setEditIndex(null);
    setEditNome("");
    setEditValor("");
    setEditDescricao("");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo center" style={fontStyle}>World Beauty</a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Serviços</h5>
        <table className="highlight centered">
          <thead>
            <tr>
              <th>Serviço</th>
              <th>Preço</th>
              <th>Descrição</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {servicos.map((servico, index) => (
              <tr key={index}>
                <td>
                  {editIndex === index ? (
                    <input type="text" value={editNome} onChange={(e) => setEditNome(e.target.value)} />
                  ) : (
                    servico.nome
                  )}
                </td>
                <td>
                  {editIndex === index ? (
                    <input type="text" value={editValor} onChange={(e) => setEditValor(e.target.value)} />
                  ) : (
                    servico.valor
                  )}
                </td>
                <td>
                  {editIndex === index ? (
                    <input type="text" value={editDescricao} onChange={(e) => setEditDescricao(e.target.value)} />
                  ) : (
                    servico.descricao
                  )}
                </td>
                <td>
                  {editIndex === index ? (
                    <>
                      <button style={botaoStyle} onClick={handleSave}>Salvar</button>
                      <button style={botaoStyle} onClick={handleCancel}>Cancelar</button>
                    </>
                  ) : (
                    <>
                      <img src="/path/to/edit-icon.png" style={ImagemStyle} onClick={() => handleEdit(index)} alt="Editar" />
                      <img src="/path/to/delete-icon.png" style={ImagemStyle} onClick={() => handleDelete(index)} alt="Excluir" />
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
