// ConsumoGenero.tsx
import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";

const ConsumoGenero = () => {
    const [consumoGenero, setConsumoGenero] = useState<any[]>([]);
    const [editando, setEditando] = useState<boolean>(false);
    const [generoEditado, setGeneroEditado] = useState<any | null>(null);

    useEffect(() => {
        setConsumoGenero([
            { id: 1, genero: "Masculino", consumo: 1200 },
            { id: 2, genero: "Feminino", consumo: 800 },
            { id: 3, genero: "Outro", consumo: 400 },
        ]);
    }, []);

    const handleRegistro = () => {
        const novoGenero = { id: consumoGenero.length + 1, genero: "Novo Gênero", consumo: 100 };
        setConsumoGenero([...consumoGenero, novoGenero]);
    };

    const handleEdição = (genero: any) => {
        setEditando(true);
        setGeneroEditado(genero);
    };

    const handleDelecao = (id: number) => {
        setConsumoGenero(consumoGenero.filter(genero => genero.id !== id));
    };

    const handleSalvarEdicao = () => {
        setConsumoGenero(consumoGenero.map(genero =>
            genero.id === generoEditado.id ? generoEditado : genero
        ));
        setEditando(false);
        setGeneroEditado(null);
    };

    return (
        <div>
            <h3>Consumo por Gênero</h3>
            <Button variant="primary" onClick={handleRegistro}>Registrar Gênero</Button>
            <ul>
                {consumoGenero.map(genero => (
                    <li key={genero.id}>
                        {editando && genero.id === generoEditado?.id ? (
                            <div>
                                <input
                                    type="text"
                                    value={generoEditado?.genero}
                                    onChange={(e) => setGeneroEditado({ ...generoEditado, genero: e.target.value })}
                                />
                                <input
                                    type="number"
                                    value={generoEditado?.consumo}
                                    onChange={(e) => setGeneroEditado({ ...generoEditado, consumo: +e.target.value })}
                                />
                                <Button variant="success" onClick={handleSalvarEdicao}>Salvar</Button>
                            </div>
                        ) : (
                            <div>
                                {genero.genero} - {genero.consumo} unidades
                                <Button variant="info" onClick={() => handleEdição(genero)}>Editar</Button>
                                <Button variant="danger" onClick={() => handleDelecao(genero.id)}>Deletar</Button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ConsumoGenero;
