import React, { useState, useEffect } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000000",
};

const containerStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
};

const navStyle: CSS.Properties = {
  width: "250px", // Largura do menu lateral
  backgroundColor: "#000",
  padding: "20px",
  height: "100vh", // Altura para preencher a tela
  display: "flex",
  flexDirection: "column", // Organizar itens na vertical
  justifyContent: "flex-start", // Alinhar ao topo
};

const logoStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
  marginBottom: "20px", // EspaÃ§o abaixo da logo
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column", // Itens do menu em coluna
  gap: "15px",
};

const botaoStyle: CSS.Properties = {
  fontFamily: "arial",
  backgroundColor: "#333333",
  color: "#fff",
  border: "none",
  cursor: "pointer",
  padding: "10px",
  borderRadius: "5px",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editData, setEditData] = useState<Partial<Consumo>>({});

  useEffect(() => {
    const consumosArmazenados = JSON.parse(localStorage.getItem("consumos") || "[]");
    setConsumos(consumosArmazenados);
  }, []);

  const handleDelete = (index: number) => {
    const updatedConsumos = [...consumos];
    updatedConsumos.splice(index, 1);
    setConsumos(updatedConsumos);
    localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
    alert("Consumo deletado com sucesso!");
  };

  const handleEdit = (index: number) => {
    setEditingIndex(index);
    setEditData(consumos[index]);
  };

  const handleSave = () => {
    if (editingIndex !== null) {
      const updatedConsumos = [...consumos];
      updatedConsumos[editingIndex] = editData as Consumo;
      setConsumos(updatedConsumos);
      localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
      setEditingIndex(null);
      alert("Consumo atualizado com sucesso!");
    }
  };

  const handleCancel = () => {
    setEditingIndex(null);
    setEditData({});
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>, field: keyof Consumo) => {
    setEditData((prevData) => ({
      ...prevData,
      [field]: e.target.value,
    }));
  };

  return (
    <div style={containerStyle}>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo center" style={logoStyle}>
            World Beauty
          </a>
          <div style={{ display: "flex", justifyContent: "center", gap: "10px" }}>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">ServiÃ§os</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </div>
      </nav>

      {/* Lista de Consumo */}
      <div className="container" style={{ marginTop: "20px" }}>
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>AÃ§Ãµes</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>
                    {editingIndex === index ? (
                      <input
                        type="text"
                        value={editData.idPedido || ""}
                        onChange={(e) => handleInputChange(e, "idPedido")}
                      />
                    ) : (
                      consumo.idPedido
                    )}
                  </td>
                  <td>
                    {editingIndex === index ? (
                      <input
                        type="text"
                        value={editData.idProduto || ""}
                        onChange={(e) => handleInputChange(e, "idProduto")}
                      />
                    ) : (
                      consumo.idProduto
                    )}
                  </td>
                  <td>
                    {editingIndex === index ? (
                      <input
                        type="text"
                        value={editData.nomeProduto || ""}
                        onChange={(e) => handleInputChange(e, "nomeProduto")}
                      />
                    ) : (
                      consumo.nomeProduto
                    )}
                  </td>
                  <td>
                    {editingIndex === index ? (
                      <input
                        type="text"
                        value={editData.valor || ""}
                        onChange={(e) => handleInputChange(e, "valor")}
                      />
                    ) : (
                      consumo.valor
                    )}
                  </td>
                  <td>
                    {editingIndex === index ? (
                      <>
                        <button onClick={handleSave} style={actionButtonStyle}>
                          Salvar
                        </button>
                        <button onClick={handleCancel} style={actionButtonStyle}>
                          Cancelar
                        </button>
                      </>
                    ) : (
                      <>
                        <button onClick={() => handleEdit(index)} style={actionButtonStyle}>
                          Editar
                        </button>
                        <button onClick={() => handleDelete(index)} style={actionButtonStyle}>
                          Deletar
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Sem registros de consumo.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
