import React, { useState } from 'react';

const ClienteConsumoValor = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
    { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
    { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", valorConsumido: 350.00 },
    { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", valorConsumido: 120.00 },
    { id: 6, nome: "Juliana Mendes", cpf: "678.901.234-55", telefone: "(61)98765-4326", valorConsumido: 900.00 },
    { id: 7, nome: "Fernanda Silva", cpf: "789.012.345-66", telefone: "(71)98765-4327", valorConsumido: 200.00 },
    { id: 8, nome: "Roberto Lima", cpf: "890.123.456-77", telefone: "(81)98765-4328", valorConsumido: 1000.00 },
    { id: 9, nome: "Luciana Alves", cpf: "901.234.567-88", telefone: "(91)98765-4329", valorConsumido: 250.00 },
    { id: 10, nome: "Ricardo Barbosa", cpf: "012.345.678-99", telefone: "(11)98765-4330", valorConsumido: 450.00 }
  ]);

  const [formData, setFormData] = useState({
    id: '',
    nome: '',
    cpf: '',
    telefone: '',
    valorConsumido: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleAddClient = () => {
    if (formData.nome && formData.cpf && formData.telefone && formData.valorConsumido) {
      const newClient = {
        ...formData,
        id: clientes.length + 1, // Gerar um novo ID
        valorConsumido: parseFloat(formData.valorConsumido)
      };
      setClientes([...clientes, newClient]);
      setFormData({ id: '', nome: '', cpf: '', telefone: '', valorConsumido: '' }); // Limpar o formulário
    }
  };

  const handleEditClient = (id) => {
    const clientToEdit = clientes.find(client => client.id === id);
    setFormData(clientToEdit); // Preencher os dados no formulário para edição
  };

  const handleDeleteClient = (id) => {
    const updatedClients = clientes.filter(client => client.id !== id);
    setClientes(updatedClients);
  };

  return (
    <div id="1" className="col s12">
      <form>
        <div>
          <table className="responsive-table centered">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Telefone</th>
                <th>Valor Consumido</th>
                <th>Ações</th> {/* Coluna de ações */}
              </tr>
            </thead>
            <tbody>
              {clientes
                .filter(cliente => cliente.valorConsumido > 300) // Filtrando valores consumidos maiores que 300
                .sort((a, b) => b.valorConsumido - a.valorConsumido) // Ordenando do maior para o menor consumo
                .map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>{cliente.nome}</td>
                    <td>{cliente.cpf}</td>
                    <td>{cliente.telefone}</td>
                    <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                    <td>
                      {/* Botões de Edição e Deleção */}
                      <button type="button" className="btn black white-text" onClick={() => handleEditClient(cliente.id)}>
                        Editar
                      </button>
                      <button type="button" className="btn black white-text" onClick={() => handleDeleteClient(cliente.id)}>
                        Deletar
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </form>

      {/* Formulário de Registro */}
      <div className="center">
        <div>
          <input
            type="text"
            name="nome"
            value={formData.nome}
            onChange={handleInputChange}
            placeholder="Nome"
          />
          <input
            type="text"
            name="cpf"
            value={formData.cpf}
            onChange={handleInputChange}
            placeholder="CPF"
          />
          <input
            type="text"
            name="telefone"
            value={formData.telefone}
            onChange={handleInputChange}
            placeholder="Telefone"
          />
          <input
            type="number"
            name="valorConsumido"
            value={formData.valorConsumido}
            onChange={handleInputChange}
            placeholder="Valor Consumido"
          />
        </div>

        <button type="button" className="btn green" onClick={handleAddClient}>Registrar</button>
      </div>
    </div>
  );
};

export default ClienteConsumoValor;
