import { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000000",
  color: "#FFFFFF",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const cancelButtonStyle: CSS.Properties = {
  backgroundColor: "red",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
    { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
    // Adicione mais clientes conforme necessário
  ]);
  const [editando, setEditando] = useState<number | null>(null);
  const [clienteEditado, setClienteEditado] = useState<any>({});

  const handleEditClick = (id: number) => {
    setEditando(id);
    const cliente = clientes.find(cliente => cliente.id === id);
    if (cliente) setClienteEditado(cliente);
  };

  const handleCancelClick = () => {
    setEditando(null);
    setClienteEditado({});
  };

  const handleSaveClick = () => {
    setClientes(prevClientes =>
      prevClientes.map(cliente =>
        cliente.id === clienteEditado.id ? clienteEditado : cliente
      )
    );
    setEditando(null);
    setClienteEditado({});
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setClienteEditado(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  useEffect(() => {
    M.AutoInit();
  }, []);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>CPF</th>
                  <th>Telefone</th>
                  <th>Valor Consumido</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {clientes.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>
                      {editando === cliente.id ? (
                        <input
                          type="text"
                          name="nome"
                          value={clienteEditado.nome || cliente.nome}
                          onChange={handleInputChange}
                        />
                      ) : (
                        cliente.nome
                      )}
                    </td>
                    <td>
                      {editando === cliente.id ? (
                        <input
                          type="text"
                          name="cpf"
                          value={clienteEditado.cpf || cliente.cpf}
                          onChange={handleInputChange}
                        />
                      ) : (
                        cliente.cpf
                      )}
                    </td>
                    <td>
                      {editando === cliente.id ? (
                        <input
                          type="text"
                          name="telefone"
                          value={clienteEditado.telefone || cliente.telefone}
                          onChange={handleInputChange}
                        />
                      ) : (
                        cliente.telefone
                      )}
                    </td>
                    <td>
                      {editando === cliente.id ? (
                        <input
                          type="number"
                          name="valorConsumido"
                          value={clienteEditado.valorConsumido || cliente.valorConsumido}
                          onChange={handleInputChange}
                        />
                      ) : (
                        `R$ ${cliente.valorConsumido.toFixed(2)}`
                      )}
                    </td>
                    <td>
                      {editando === cliente.id ? (
                        <>
                          <button style={actionButtonStyle} onClick={handleSaveClick}>Salvar</button>
                          <button style={cancelButtonStyle} onClick={handleCancelClick}>Cancelar</button>
                        </>
                      ) : (
                        <button style={actionButtonStyle} onClick={() => handleEditClick(cliente.id)}>Editar</button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
