import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

// Define styles (move them to a separate CSS or styled-components for better practices)
const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState<any[]>([]); // Use state to store client data

  useEffect(() => {
    // Initialize Materialize components
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);

    // Sample data (move to a dynamic source if necessary)
    const clientData = [
      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500 },
      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150 },
      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800 },
      { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", valorConsumido: 350 },
      { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", valorConsumido: 120 },
      { id: 6, nome: "Juliana Mendes", cpf: "678.901.234-55", telefone: "(61)98765-4326", valorConsumido: 900 },
      { id: 7, nome: "Fernanda Silva", cpf: "789.012.345-66", telefone: "(71)98765-4327", valorConsumido: 200 },
      { id: 8, nome: "Roberto Lima", cpf: "890.123.456-77", telefone: "(81)98765-4328", valorConsumido: 1000 },
      { id: 9, nome: "Luciana Alves", cpf: "901.234.567-88", telefone: "(91)98765-4329", valorConsumido: 250 },
      { id: 10, nome: "Ricardo Barbosa", cpf: "012.345.678-99", telefone: "(11)98765-4330", valorConsumido: 450 },
    ];

    setClientes(clientData);
  }, []);

  // Filtered and sorted client data
  const clientesFiltrados = clientes
    .filter(cliente => cliente.valorConsumido > 300)
    .sort((a, b) => b.valorConsumido - a.valorConsumido);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <div className="row">
          <div className="tabs-swipe-demo tabs tabsConsumo">
            <ul className="tabs">
              <li className="tab col s3"><a href="#1">Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2">Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3">Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4">Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5">Consumo Gênero</a></li>
            </ul>
          </div>

          <div id="1" className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>CPF</th>
                  <th>Telefone</th>
                  <th>Valor Consumido</th>
                </tr>
              </thead>
              <tbody>
                {clientesFiltrados.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>{cliente.nome}</td>
                    <td>{cliente.cpf}</td>
                    <td>{cliente.telefone}</td>
                    <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
