import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";
import axios from 'axios';

// Estilos (mantidos como estavam)
const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};
const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};
const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};
const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

// Interfaces para os tipos de dados
interface Cliente {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  valorConsumido: number;
  qtdConsumida?: number;
}

interface ProdutoServico {
  id: number;
  nome: string;
  valor: number;
}

interface ConsumoGenero {
  id: number;
  nome: string;
  produto: string;
  valor: number;
}

// Funções para buscar dados do backend
const getClientesPorValor = async (): Promise<Cliente[]> => {
  const response = await axios.get<Cliente[]>('http://localhost:5000/api/clientes/por-valor');
  return response.data;
};

const getClientesPorQuantidade = async (): Promise<Cliente[]> => {
  const response = await axios.get<Cliente[]>('http://localhost:5000/api/clientes/por-quantidade');
  return response.data;
};

const getClientesMenorConsumo = async (): Promise<Cliente[]> => {
  const response = await axios.get<Cliente[]>('http://localhost:5000/api/clientes/menor-consumo');
  return response.data;
};

const getProdutosServicos = async (): Promise<ProdutoServico[]> => {
  const response = await axios.get<ProdutoServico[]>('http://localhost:5000/api/produtos-servicos');
  return response.data;
};

const getConsumoGeneroFeminino = async (): Promise<ConsumoGenero[]> => {
  const response = await axios.get<ConsumoGenero[]>('http://localhost:5000/api/consumo/feminino');
  return response.data;
};

const getConsumoGeneroMasculino = async (): Promise<ConsumoGenero[]> => {
  const response = await axios.get<ConsumoGenero[]>('http://localhost:5000/api/consumo/masculino');
  return response.data;
};

const Listagens = () => {
  const [clientesPorValor, setClientesPorValor] = useState<Cliente[]>([]);
  const [clientesPorQuantidade, setClientesPorQuantidade] = useState<Cliente[]>([]);
  const [clientesMenorConsumo, setClientesMenorConsumo] = useState<Cliente[]>([]);
  const [produtosServicos, setProdutosServicos] = useState<ProdutoServico[]>([]);
  const [consumoFeminino, setConsumoFeminino] = useState<ConsumoGenero[]>([]);
  const [consumoMasculino, setConsumoMasculino] = useState<ConsumoGenero[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [valorData, qtdData, menorData, prodServData, femData, mascData] = await Promise.all([
          getClientesPorValor(),
          getClientesPorQuantidade(),
          getClientesMenorConsumo(),
          getProdutosServicos(),
          getConsumoGeneroFeminino(),
          getConsumoGeneroMasculino()
        ]);

        setClientesPorValor(valorData);
        setClientesPorQuantidade(qtdData);
        setClientesMenorConsumo(menorData);
        setProdutosServicos(prodServData);
        setConsumoFeminino(femData);
        setConsumoMasculino(mascData);
      } catch (err) {
        setError("Erro ao carregar dados. Por favor, tente novamente mais tarde.");
        console.error("Erro ao buscar dados:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  if (loading) {
    return <div>Carregando...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor Consumido</th>
                    </tr>
                  </thead>
                  <tbody>
                    {clientesPorValor.map(cliente => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Quantidade Consumida</th>
                    </tr>
                  </thead>
                  <tbody>
                    {clientesPorQuantidade.map(cliente => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>{cliente.qtdConsumida}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor Consumido</th>
                    </tr>
                  </thead>
                  <tbody>
                    {clientesMenorConsumo.map(cliente => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Produto/Serviço Consumo */}
          <div id="4" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome do Produto/Serviço</th>
                      <th>Valor do Produto/Serviço</th>
                    </tr>
                  </thead>
                  <tbody>
                    {produtosServicos.map(item => (
                      <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>R$ {item.valor.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Consumo por Gênero */}
          <div id="5" className="col s12">
            <form>
              <h5 className="generoConsumoFont center-align">Feminino</h5>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome da Cliente</th>
                      <th>Produto/Serviço Consumido</th>
                      <th className="valor-column">Valor do Produto/Serviço</th>
                    </tr>
                  </thead>
                  <tbody>
                    {consumoFeminino.map(item => (
                      <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.produto}</td>
                        <td className="valor-column">R$ {item.valor.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <br />
              <h5 className="generoConsumoFont center-align">Masculino</h5>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome do Cliente</th>
                      <th>Produto/Serviço Consumido</th>
                      <th className="valor-column">Valor do Produto/Serviço</th>
                    </tr>
                  </thead>
                  <tbody>
                    {consumoMasculino.map(item => (
                      <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.produto}</td>
                        <td className="valor-column">R$ {item.valor.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <br />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
