import { useState, useEffect } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  cursor: "pointer",
};

const ListaServicos = () => {
  const [servicos, setServicos] = useState<{ nome: string; valor: string }[]>([]);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editNome, setEditNome] = useState("");
  const [editValor, setEditValor] = useState("");

  useEffect(() => {
    const savedServices = JSON.parse(localStorage.getItem("servicos") || "[]");
    setServicos(savedServices);
  }, []);

  const handleDelete = (index: number) => {
    const updatedServices = servicos.filter((_, i) => i !== index);
    localStorage.setItem("servicos", JSON.stringify(updatedServices));
    setServicos(updatedServices);
  };

  const handleEdit = (index: number) => {
    setEditingIndex(index);
    setEditNome(servicos[index].nome);
    setEditValor(servicos[index].valor);
  };

  const handleSave = (index: number) => {
    const updatedServices = [...servicos];
    updatedServices[index] = { nome: editNome, valor: editValor };
    localStorage.setItem("servicos", JSON.stringify(updatedServices));
    setServicos(updatedServices);
    setEditingIndex(null);
  };

  const handleCancel = () => {
    setEditingIndex(null);
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">
            Cadastro de Serviço
          </a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Serviços</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Serviço</th>
              <th>Preço</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {servicos.map((servico, index) => (
              <tr key={index}>
                {editingIndex === index ? (
                  <>
                    <td>
                      <input
                        value={editNome}
                        onChange={(e) => setEditNome(e.target.value)}
                      />
                    </td>
                    <td>
                      <input
                        value={editValor}
                        onChange={(e) => setEditValor(e.target.value)}
                      />
                    </td>
                    <td>
                      <button onClick={() => handleSave(index)} style={botaoStyle}>
                        Salvar
                      </button>
                      <button onClick={handleCancel} style={botaoStyle}>
                        Cancelar
                      </button>
                    </td>
                  </>
                ) : (
                  <>
                    <td>{servico.nome}</td>
                    <td>{servico.valor}</td>
                    <td>
                      <img
                        src="editar-preto.png" // Substitua pela imagem de edição preta
                        alt="editar"
                        style={ImagemStyle}
                        onClick={() => handleEdit(index)}
                      />
                      <img
                        src="excluir-preto.png" // Substitua pela imagem de exclusão preta
                        alt="excluir"
                        style={ImagemStyle}
                        onClick={() => handleDelete(index)}
                      />
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaServicos;
