import React, { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

// Definir tipo de Produto
type Produto = {
  nome: string;
  valor: string;
};

// Definir o tipo de Props, sem parâmetros específicos neste caso
type Props = {
  tema?: string; // O tema pode ser opcional, caso contrário, remova o '?'
};

// Definir o tipo de State
type State = {
  produtos: Produto[];
  isEditing: boolean;
  editIndex: number;
  editNome: string;
  editPreco: string;
};

// Estilos em CSS inline
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000", // Preto
  color: "#fff", // Texto branco
  padding: "8px 16px", // Ajuste de padding para botões
  border: "none",
  cursor: "pointer",
  marginLeft: "10px", // Espaçamento entre os botões
};

class ListaProdutos extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    // Carregar os produtos do localStorage e inicializar o estado
    const produtos = JSON.parse(localStorage.getItem('produtos') || '[]');
    this.state = {
      produtos,
      isEditing: false,
      editIndex: -1,
      editNome: '',
      editPreco: ''
    };
  }

  // Função para editar o produto
  handleEdit = (index: number) => {
    const produto = this.state.produtos[index];
    this.setState({
      isEditing: true,
      editIndex: index,
      editNome: produto.nome,
      editPreco: produto.valor
    });
  };

  // Função para salvar a edição
  handleSaveEdit = () => {
    const { produtos, editIndex, editNome, editPreco } = this.state;
    const updatedProdutos = [...produtos];
    updatedProdutos[editIndex] = { nome: editNome, valor: editPreco };

    localStorage.setItem('produtos', JSON.stringify(updatedProdutos));
    this.setState({
      produtos: updatedProdutos,
      isEditing: false,
      editIndex: -1,
      editNome: '',
      editPreco: ''
    });
  };

  // Função para deletar o produto
  handleDelete = (index: number) => {
    const produtos = this.state.produtos;
    produtos.splice(index, 1); // Remove o produto pelo índice
    localStorage.setItem('produtos', JSON.stringify(produtos)); // Atualiza o localStorage
    this.setState({ produtos }); // Atualiza o estado para re-renderizar
    alert('Produto deletado com sucesso!');
  };

  // Função para lidar com mudanças nos campos de edição
  handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (name === 'nome') {
      this.setState({ editNome: value });
    } else if (name === 'preco') {
      this.setState({ editPreco: value });
    }
  };

  render() {
    const { produtos, isEditing, editNome, editPreco } = this.state;

    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>World Beauty</a>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">Serviços</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Lista de Produtos</h5>

          {/* Exibir o formulário de edição se for edição */}
          {isEditing && (
            <div>
              <h6>Editar Produto</h6>
              <input
                type="text"
                name="nome"
                value={editNome}
                onChange={this.handleInputChange}
                placeholder="Nome do Produto"
              />
              <input
                type="text"
                name="preco"
                value={editPreco}
                onChange={this.handleInputChange}
                placeholder="Preço do Produto"
              />
              <button onClick={this.handleSaveEdit} style={actionButtonStyle}>
                Salvar
              </button>
              <button
                onClick={() => this.setState({ isEditing: false })}
                style={actionButtonStyle}
              >
                Cancelar
              </button>
            </div>
          )}

          <table className="striped centered">
            <thead>
              <tr>
                <th>Produto</th>
                <th>Preço</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {produtos.length > 0 ? (
                produtos.map((produto: Produto, index: number) => (
                  <tr key={index}>
                    <td>{produto.nome}</td>
                    <td>{produto.valor}</td>
                    <td style={{ textAlign: "right" }}>
                      {/* Botões de Editar e Deletar alinhados à direita */}
                      <button
                        onClick={() => this.handleEdit(index)}
                        style={actionButtonStyle}
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => this.handleDelete(index)}
                        style={actionButtonStyle}
                      >
                        Deletar
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={3}>Nenhum produto cadastrado</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaProdutos;
