import { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

// Definindo o tipo de cada serviÃ§o no estado
type Servico = {
  nome: string;
  valor: string;
  descricao: string;
};

type State = {
  servicos: Servico[]; // O estado agora Ã© uma lista de objetos do tipo 'Servico'
  editIndex: number | null; // Ãndice do serviÃ§o sendo editado
  novoNome: string;
  novoValor: string;
  novaDescricao: string;
};

class ListaServicos extends Component<{}, State> {
  state: State = {
    servicos: [],
    editIndex: null,
    novoNome: "",
    novoValor: "",
    novaDescricao: "",
  };

  componentDidMount() {
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    this.setState({ servicos });
  }

  handleDelete = (index: number) => {
    const servicos = [...this.state.servicos];
    servicos.splice(index, 1);
    this.setState({ servicos });
    localStorage.setItem("servicos", JSON.stringify(servicos));
  };

  handleEdit = (index: number) => {
    const servico = this.state.servicos[index];
    this.setState({
      editIndex: index,
      novoNome: servico.nome,
      novoValor: servico.valor,
      novaDescricao: servico.descricao,
    });
  };

  handleSave = () => {
    const { editIndex, novoNome, novoValor, novaDescricao } = this.state;
    const servicos = [...this.state.servicos];
    servicos[editIndex!] = { nome: novoNome, valor: novoValor, descricao: novaDescricao };
    this.setState({ servicos, editIndex: null, novoNome: "", novoValor: "", novaDescricao: "" });
    localStorage.setItem("servicos", JSON.stringify(servicos));
  };

  handleCancel = () => {
    this.setState({ editIndex: null, novoNome: "", novoValor: "", novaDescricao: "" });
  };

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaPedidos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              ServiÃ§os
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Lista de ServiÃ§os</h5>
          <table className="striped centered">
            <thead>
              <tr>
                <th>ServiÃ§o</th>
                <th>PreÃ§o</th>
                <th>DescriÃ§Ã£o</th>
                <th>AÃ§Ãµes</th>
              </tr>
            </thead>
            <tbody>
              {this.state.servicos.length === 0 ? (
                <tr>
                  <td colSpan={4} className="center-align">
                    Nenhum serviÃ§o cadastrado
                  </td>
                </tr>
              ) : (
                this.state.servicos.map((servico, index) => (
                  <tr key={index}>
                    <td>
                      {this.state.editIndex === index ? (
                        <input
                          type="text"
                          value={this.state.novoNome}
                          onChange={(e) => this.setState({ novoNome: e.target.value })}
                        />
                      ) : (
                        servico.nome
                      )}
                    </td>
                    <td>
                      {this.state.editIndex === index ? (
                        <input
                          type="text"
                          value={this.state.novoValor}
                          onChange={(e) => this.setState({ novoValor: e.target.value })}
                        />
                      ) : (
                        servico.valor
                      )}
                    </td>
                    <td>
                      {this.state.editIndex === index ? (
                        <input
                          type="text"
                          value={this.state.novaDescricao}
                          onChange={(e) => this.setState({ novaDescricao: e.target.value })}
                        />
                      ) : (
                        servico.descricao
                      )}
                    </td>
                    <td>
                      {this.state.editIndex === index ? (
                        <span>
                          <button onClick={this.handleSave} style={botaoStyle}>
                            Salvar
                          </button>
                          <button onClick={this.handleCancel} style={botaoStyle}>
                            Cancelar
                          </button>
                        </span>
                      ) : (
                        <span>
                          <button onClick={() => this.handleEdit(index)} style={botaoStyle}>
                            Editar
                          </button>
                          <button onClick={() => this.handleDelete(index)} style={botaoStyle}>
                            Deletar
                          </button>
                        </span>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaServicos;
