import { useState } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

export default function FormularioCadastroServico() {
  const [nome, setNome] = useState("");
  const [valor, setValor] = useState("");
  const [descricao, setDescricao] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    servicos.push({ nome, valor, descricao });
    localStorage.setItem("servicos", JSON.stringify(servicos));
    setNome("");
    setValor("");
    setDescricao("");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="input-field">
            <input id="nome_servico" type="text" value={nome} onChange={(e) => setNome(e.target.value)} required />
            <label htmlFor="nome_servico">Nome do Serviço</label>
          </div>
          <div className="input-field">
            <input id="valor_servico" type="text" value={valor} onChange={(e) => setValor(e.target.value)} required />
            <label htmlFor="valor_servico">Valor do Serviço</label>
          </div>
          <div className="input-field">
            <input id="descricao_servico" type="text" value={descricao} onChange={(e) => setDescricao(e.target.value)} required />
            <label htmlFor="descricao_servico">Descrição do Serviço</label>
          </div>
          <button style={botaoStyle} type="submit">Registrar</button>
        </form>
      </div>
    </div>
  );
}
