import React, { useState } from "react";
import { Consumo } from "./ListaConsumo"; // Supondo que você tenha esse tipo no arquivo de ListaConsumo.

const CadastroConsumo: React.FC<{ addConsumo: (consumo: Consumo) => void }> = ({ addConsumo }) => {
  const [idPedido, setIdPedido] = useState("");
  const [idProduto, setIdProduto] = useState("");
  const [nomeProduto, setNomeProduto] = useState("");
  const [valor, setValor] = useState("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const novoConsumo: Consumo = { idPedido, idProduto, nomeProduto, valor };
    addConsumo(novoConsumo); // Chama a função para adicionar o consumo
    setIdPedido(""); // Limpa os campos
    setIdProduto("");
    setNomeProduto("");
    setValor("");
  };

  return (
    <div>
      <h4>Cadastrar Consumo</h4>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={idPedido}
          onChange={(e) => setIdPedido(e.target.value)}
          placeholder="Id do Pedido"
          required
        />
        <input
          type="text"
          value={idProduto}
          onChange={(e) => setIdProduto(e.target.value)}
          placeholder="Id do Produto"
          required
        />
        <input
          type="text"
          value={nomeProduto}
          onChange={(e) => setNomeProduto(e.target.value)}
          placeholder="Nome do Produto"
          required
        />
        <input
          type="text"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          placeholder="Valor"
          required
        />
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
};

export default CadastroConsumo;
