import React, { Component } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom"; // Atualização para React Router v6
import CSS from "csstype";

type props = {
  tema: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000000", // Preto
};

const botaoStyle: CSS.Properties = {
  padding: "12px 20px", // Ajuste no padding para botões maiores e mais confortáveis
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333", // Um tom de cinza para o fundo do botão
  color: "#FFFFFF", // Texto branco
  border: "none", // Removendo bordas para um visual mais limpo
  borderRadius: "5px", // Bordas arredondadas
  cursor: "pointer", // Indicação de clique
  transition: "background-color 0.3s ease", // Transição suave para o hover
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF", // Texto branco
};

// Componente de Formulário de Cadastro de Produtos
class FormularioCadastroProduto extends Component<any, props> {
  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo center" style={fontStyle}>
              World Beauty
            </a>

            {/* Substituindo <a> por <Link> para navegação sem recarregar a página */}
            <Link style={botaoStyle} to="/listaCliente">
              Cliente
            </Link>
            <Link style={botaoStyle} to="/listaProduto">
              Produto
            </Link>
            <Link style={botaoStyle} to="/listaPedidos">
              Consumo
            </Link>
            <Link style={botaoStyle} to="/listaServicos">
              Serviços
            </Link>
            <Link style={botaoStyle} to="/Listagens">
              Listagens
            </Link>
            <Link style={botaoStyle} to="/formularioCadastroProduto">
              Cadastrar produtos
            </Link>
          </div>
        </nav>

        <br />
        <div className="container">
          <div className="row">
            <form className="col s12">
              <div className="row">
                <div className="input-field col s6">
                  <input id="nome_produto" type="text" className="validate" />
                  <label htmlFor="nome_produto">Nome</label>
                </div>
                <div className="input-field col s6">
                  <input id="preco" type="text" className="validate" />
                  <label htmlFor="preco">Preço</label>
                </div>
              </div>
              <div className="row">
                <div className="col s12">
                  <button
                    className="btn waves-effect waves-light"
                    type="submit"
                    name="action"
                  >
                    Registrar
                    <i className="material-icons right">send</i>
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

// Componente de Cadastro de Cliente (exemplo)
class FormularioCadastroCliente extends Component {
  render() {
    return (
      <div>
        <h2>Cadastro de Cliente</h2>
        {/* Formulário de cadastro de cliente */}
      </div>
    );
  }
}

// Componente Principal (App.js ou App.tsx)
class App extends Component {
  render() {
    return (
      <Router>
        <div>
          <Routes>
            {/* Definindo as rotas para os diferentes formulários */}
            <Route path="/formularioCadastroProduto" element={<FormularioCadastroProduto />} />
            <Route path="/listaCliente" element={<FormularioCadastroCliente />} />
            {/* Defina outras rotas conforme necessário */}
          </Routes>
        </div>
      </Router>
    );
  }
}

export default App;
