import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Consumo = {
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);

  // Carrega produtos do localStorage ao montar o componente
  useEffect(() => {
    const produtos = JSON.parse(localStorage.getItem("produtos") || "[]");
    setConsumos(produtos);
  }, []);

  const handleDelete = (index: number) => {
    const updatedConsumos = [...consumos];
    updatedConsumos.splice(index, 1);
    setConsumos(updatedConsumos);
    localStorage.setItem("produtos", JSON.stringify(updatedConsumos)); // Atualiza o localStorage
    alert("Consumo deletado com sucesso!");
  };

  return (
    <div>
      <div className="container">
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.idProduto}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td style={{ textAlign: "right" }}>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>
                      Deletar
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={4}>Sem registros de consumo.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
