import React, { useState } from "react";
import CSS from "csstype";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
  dataNascimento: string; // Adicionando o campo de data de aniversário
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const CadastroCliente = () => {
  const [nome, setNome] = useState("");
  const [cpf, setCpf] = useState("");
  const [rg, setRg] = useState("");
  const [dataEmissao, setDataEmissao] = useState("");
  const [telefone, setTelefone] = useState("");
  const [dataCadastro, setDataCadastro] = useState("");
  const [genero, setGenero] = useState("");
  const [dataNascimento, setDataNascimento] = useState(""); // Estado para o campo de data de nascimento

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (name === "nome") setNome(value);
    if (name === "cpf") setCpf(value);
    if (name === "rg") setRg(value);
    if (name === "dataEmissao") setDataEmissao(value);
    if (name === "telefone") setTelefone(value);
    if (name === "dataCadastro") setDataCadastro(value);
    if (name === "genero") setGenero(value);
    if (name === "dataNascimento") setDataNascimento(value); // Atualizando o estado de data de nascimento
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const clienteNovo: Cliente = {
      id: String(Date.now()),
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
      dataNascimento, // Incluindo a data de nascimento
    };

    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    clientes.push(clienteNovo);
    localStorage.setItem("clientes", JSON.stringify(clientes));

    setNome("");
    setCpf("");
    setRg("");
    setDataEmissao("");
    setTelefone("");
    setDataCadastro("");
    setGenero("");
    setDataNascimento(""); // Limpar o campo de data de nascimento

    alert("Cliente cadastrado com sucesso!");
  };

  return (
    <div>
      <nav className="">
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>

          <a style={botaoStyle} href="/cadServico">
            Cadastros
          </a>
          <a style={botaoStyle} href="/listaCliente">
            Cliente
          </a>
          <a style={botaoStyle} href="/listaProduto">
            Produto
          </a>
          <a style={botaoStyle} href="/listaPedidos">
            Consumo
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Serviços
          </a>
          <a style={botaoStyle} href="/Listagens">
            Listagens
          </a>
        </div>
      </nav>

      <div className="container">
        <h5 className="center-align">Cadastro de Cliente</h5>
        <form onSubmit={handleSubmit}>
          <div className="input-field">
            <label>Nome:</label>
            <input
              type="text"
              name="nome"
              value={nome}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-field">
            <label>CPF:</label>
            <input
              type="text"
              name="cpf"
              value={cpf}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-field">
            <label>RG:</label>
            <input
              type="text"
              name="rg"
              value={rg}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-field">
            <label>Data Emissão:</label>
            <input
              type="text"
              name="dataEmissao"
              value={dataEmissao}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-field">
            <label>Telefone:</label>
            <input
              type="text"
              name="telefone"
              value={telefone}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-field">
            <label>Data Cadastro:</label>
            <input
              type="text"
              name="dataCadastro"
              value={dataCadastro}
              onChange={handleChange}
              required
            />
          </div>

          {/* Campo de Data de Nascimento */}
          <div className="input-field">
            <label>Data de Nascimento:</label>
            <input
              type="date"
              name="dataNascimento"
              value={dataNascimento}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-field">
            <label htmlFor="genero" style={{ paddingLeft: "10px" }}>
              Gênero:
            </label>
            <div className="row" style={{ paddingLeft: "20px", marginLeft: "40px" }}>
              <div className="col s12">
                <label>
                  <input
                    name="genero"
                    type="radio"
                    value="Masculino"
                    checked={genero === "Masculino"}
                    onChange={handleChange}
                  />
                  <span>Masculino</span>
                </label>
              </div>
              <div className="col s12">
                <label>
                  <input
                    name="genero"
                    type="radio"
                    value="Feminino"
                    checked={genero === "Feminino"}
                    onChange={handleChange}
                  />
                  <span>Feminino</span>
                </label>
              </div>
              <div className="col s12">
                <label>
                  <input
                    name="genero"
                    type="radio"
                    value="Outro"
                    checked={genero === "Outro"}
                    onChange={handleChange}
                  />
                  <span>Outro</span>
                </label>
              </div>
            </div>
          </div>

          <button type="submit" style={botaoStyle}>
            Registrar Cliente
          </button>
        </form>
      </div>
    </div>
  );
};

export default CadastroCliente;
