import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import CSS from "csstype";

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const CadastroConsumo: React.FC = () => {
  const navigate = useNavigate();

  // Dados dos clientes
  const clienteA = { idCliente: "1", nome: "Cliente A", dataNascimento: "1985-06-15", dataEmissao: "" };
  const clienteB = { idCliente: "2", nome: "Cliente B", dataNascimento: "1990-03-22", dataEmissao: "" };
  const clienteC = { idCliente: "3", nome: "Cliente C", dataNascimento: "1978-11-05", dataEmissao: "" };

  const [clienteSelecionado, setClienteSelecionado] = useState(clienteA);

  const handleSelectCliente = (cliente: any) => {
    setClienteSelecionado(cliente);
  };

  const handleSubmit = () => {
    navigate("/listaPedidos", { state: { novoConsumo: clienteSelecionado } });
  };

  return (
    <div className="container">
      <h5 className="center-align">Cadastro de Consumo</h5>

      <div className="row">
        <div className="col s4">
          <button
            onClick={() => handleSelectCliente(clienteA)}
            className="btn"
            style={botaoStyle}
          >
            Cliente A
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleSelectCliente(clienteB)}
            className="btn"
            style={botaoStyle}
          >
            Cliente B
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleSelectCliente(clienteC)}
            className="btn"
            style={botaoStyle}
          >
            Cliente C
          </button>
        </div>
      </div>

      <div className="row">
        <div className="col s6">
          <label>Data de Emissão</label>
          <input
            type="date"
            value={clienteSelecionado.dataEmissao}
            onChange={(e) => setClienteSelecionado({ ...clienteSelecionado, dataEmissao: e.target.value })}
          />
        </div>
        <div className="col s6">
          <label>Data de Nascimento</label>
          <input type="text" value={clienteSelecionado.dataNascimento} readOnly />
        </div>
      </div>

      <button type="button" onClick={handleSubmit} className="btn" style={botaoStyle}>
        Registrar
      </button>
    </div>
  );
};

export default CadastroConsumo;
