import { Component } from "react";
import CSS from "csstype";

// Definir tipo de Cliente
type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
};

// Estilos em CSS inline
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

export default class CadastroCliente extends Component {
  state = {
    nome: "",
    cpf: "",
    rg: "",
    dataEmissao: "",
    telefone: "",
    dataCadastro: "",
    genero: "",
  };

  handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as any);
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro, genero } = this.state;

    const clienteNovo = {
      id: String(Date.now()), // Gerar ID único baseado no timestamp
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
    };

    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    clientes.push(clienteNovo);
    localStorage.setItem("clientes", JSON.stringify(clientes));

    this.setState({
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
      genero: "",
    });

    alert("Cliente cadastrado com sucesso!");
  };

  render() {
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro, genero } = this.state;

    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>World Beauty</a>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">Serviços</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Cadastro de Cliente</h5>
          <form onSubmit={this.handleSubmit}>
            <div className="input-field">
              <input
                type="text"
                name="nome"
                value={nome}
                onChange={this.handleChange}
                placeholder="Nome do Cliente"
                className="validate"
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                name="cpf"
                value={cpf}
                onChange={this.handleChange}
                placeholder="CPF"
                className="validate"
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                name="rg"
                value={rg}
                onChange={this.handleChange}
                placeholder="RG"
                className="validate"
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                name="dataEmissao"
                value={dataEmissao}
                onChange={this.handleChange}
                placeholder="Data de Emissão (DD/MM/AAAA)"
                className="validate"
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                name="telefone"
                value={telefone}
                onChange={this.handleChange}
                placeholder="Telefone"
                className="validate"
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                name="dataCadastro"
                value={dataCadastro}
                onChange={this.handleChange}
                placeholder="Data de Cadastro (DD/MM/AAAA)"
                className="validate"
              />
            </div>
            <div className="input-field">
              <select
                name="genero"
                value={genero}
                onChange={this.handleChange}
                className="browser-default"
              >
                <option value="" disabled>Escolha o Gênero</option>
                <option value="Masculino">Masculino</option>
                <option value="Feminino">Feminino</option>
                <option value="Outro">Outro</option>
              </select>
            </div>
            <button type="submit" style={botaoStyle}>
              Cadastrar Cliente
            </button>
          </form>
        </div>
      </div>
    );
  }
}
