import React from "react";
import CSS from "csstype";
import editar from "../Assets/edit.png"; // Imagem de editar
import excluir from "../Assets/delete.png"; // Imagem de excluir

type Pedido = {
  id: number;
  nomeProduto: string;
  valor: number;
  nomeServico: string;
  idProduto: number;
  idServico: number;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "Arial, sans-serif",
  color: "#fff", // Texto branco
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

interface Props {
  pedidos: Pedido[];
  onEditar: (id: number) => void;
  onExcluir: (id: number) => void;
}

const ListaPedidos: React.FC<Props> = ({ pedidos, onEditar, onExcluir }) => {
  return (
    <div>
      <h5 className="center-align">Pedidos</h5>
      <table className="highlight centered">
        <thead>
          <tr>
            <th>Id_Pedido</th>
            <th>Nome Produto</th>
            <th>Valor</th>
            <th>Nome Serviço</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map((pedido) => (
            <tr key={pedido.id}>
              <td>{pedido.id}</td>
              <td>{pedido.nomeProduto}</td>
              <td>{pedido.valor}</td>
              <td>{pedido.nomeServico}</td>
              <td>
                <img
                  src={editar}
                  alt="Editar"
                  style={ImagemStyle}
                  onClick={() => onEditar(pedido.id)}
                />
                <img
                  src={excluir}
                  alt="Excluir"
                  style={ImagemStyle}
                  onClick={() => onExcluir(pedido.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListaPedidos;
