import React from "react";
import { useNavigate } from "react-router-dom";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#49786d",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#49786d",
};

const PaginaInicial: React.FC = () => {
  const navigate = useNavigate(); // Hook de navegação
  const [activeTab, setActiveTab] = React.useState("cliente");

  // Função para mudar a aba ativa
  const setActiveTabHandler = (tab: string) => {
    setActiveTab(tab);
  };

  // Função para redirecionar para a página listCliente
  const handleEntrar = () => {
    navigate("/listCliente"); // Redireciona para a página listCliente
  };

  return (
    <div>
      <nav style={backgroundColor}>
        <div className="nav-wrapper">
          <a className="brand-logo center" style={fontStyle}>
            World Beauty
          </a>
        </div>
      </nav>
      <br />
      <div className="container">
        <div className="center-align">
          <div className="tabs">
            <button
              style={botaoStyle}
              className={`tab-button ${activeTab === "cliente" ? "active" : ""}`}
              onClick={() => setActiveTabHandler("cliente")}
            >
              Cadastrar Clientes
            </button>
            <button
              style={botaoStyle}
              className={`tab-button ${activeTab === "produto" ? "active" : ""}`}
              onClick={() => setActiveTabHandler("produto")}
            >
              Cadastrar Produtos
            </button>
            <button
              style={botaoStyle}
              className={`tab-button ${activeTab === "servico" ? "active" : ""}`}
              onClick={() => setActiveTabHandler("servico")}
            >
              Cadastrar Serviços
            </button>
            <button
              style={botaoStyle}
              className={`tab-button ${activeTab === "pedido" ? "active" : ""}`}
              onClick={() => setActiveTabHandler("pedido")}
            >
              Cadastrar Consumo
            </button>
          </div>

          <div className="tab-content">
            {activeTab === "cliente" && (
              <div>
                <h3>Cadastrar Cliente</h3>
                <p>Aqui você pode cadastrar um cliente.</p>
                <a href="/formularioCadastroCliente" className="waves-effect waves-light btn">
                  Acessar Cadastro de Clientes
                </a>
              </div>
            )}
            {activeTab === "produto" && (
              <div>
                <h3>Cadastrar Produto</h3>
                <p>Aqui você pode cadastrar um produto.</p>
                <a href="/formularioCadastroProduto" className="waves-effect waves-light btn">
                  Acessar Cadastro de Produtos
                </a>
              </div>
            )}
            {activeTab === "servico" && (
              <div>
                <h3>Cadastrar Serviço</h3>
                <p>Aqui você pode cadastrar um serviço.</p>
                <a href="/formularioCadastroServico" className="waves-effect waves-light btn">
                  Acessar Cadastro de Serviços
                </a>
              </div>
            )}
            {activeTab === "pedido" && (
              <div>
                <h3>Cadastrar Consumo</h3>
                <p>Aqui você pode cadastrar um consumo.</p>
                <a href="/formularioCadastroPedido" className="waves-effect waves-light btn">
                  Acessar Cadastro de Consumo
                </a>
              </div>
            )}
          </div>

          {/* Botão "ENTRAR" que redireciona para a página listCliente */}
          <br />
          <button onClick={handleEntrar} style={botaoStyle} className="waves-effect waves-light btn">
            ENTRAR
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaginaInicial;
