import React, { useState, useEffect } from "react";
import CSS from "csstype";
import { Link } from "react-router-dom";
import "materialize-css/dist/css/materialize.min.css";

// Estilos CSS
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([
    {
      idPedido: "001",
      idProduto: "P001",
      nomeProduto: "Produto 1",
      valor: "100,00",
    },
    {
      idPedido: "002",
      idProduto: "P002",
      nomeProduto: "Produto 2",
      valor: "150,00",
    },
    // Adicione mais consumos conforme necessário
  ]);
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [editNomeProduto, setEditNomeProduto] = useState("");
  const [editValor, setEditValor] = useState("");

  const handleEdit = (index: number) => {
    const consumo = consumos[index];
    setIsEditing(true);
    setEditIndex(index);
    setEditNomeProduto(consumo.nomeProduto);
    setEditValor(consumo.valor);
  };

  const handleSaveEdit = () => {
    const updatedConsumos = [...consumos];
    if (editIndex !== null) {
      updatedConsumos[editIndex] = {
        ...updatedConsumos[editIndex],
        nomeProduto: editNomeProduto,
        valor: editValor,
      };
      setConsumos(updatedConsumos);
      setIsEditing(false);
      setEditIndex(null);
      setEditNomeProduto("");
      setEditValor("");
    }
  };

  const handleDelete = (index: number) => {
    const updatedConsumos = [...consumos];
    updatedConsumos.splice(index, 1);
    setConsumos(updatedConsumos);
    alert("Consumo deletado com sucesso!");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Consumo</h5>

        {isEditing && (
          <div>
            <h6>Editar Consumo</h6>
            <input
              type="text"
              value={editNomeProduto}
              onChange={(e) => setEditNomeProduto(e.target.value)}
              placeholder="Nome do Produto"
            />
            <input
              type="text"
              value={editValor}
              onChange={(e) => setEditValor(e.target.value)}
              placeholder="Valor"
            />
            <button onClick={handleSaveEdit} style={actionButtonStyle}>Salvar</button>
            <button onClick={() => setIsEditing(false)} style={actionButtonStyle}>Cancelar</button>
          </div>
        )}

        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.idPedido}</td>
                  <td>{consumo.idProduto}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td style={{ textAlign: "right" }}>
                    <button onClick={() => handleEdit(index)} style={actionButtonStyle}>Editar</button>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Nenhum consumo registrado</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
