import React, { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

// Estilos CSS
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

// Tipos
type Consumo = {
  idPedido: string;
  cliente: string;
  servico: string;
  produto: string;
  valor: string;
};

const App: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);
  const [cliente, setCliente] = useState("");
  const [servico, setServico] = useState("");
  const [produto, setProduto] = useState("");
  const [precoServico, setPrecoServico] = useState("");

  useEffect(() => {
    M.FormSelect.init(document.querySelectorAll("select"));
  }, []);

  const handleRegister = () => {
    if (!cliente || !servico || !produto || !precoServico) {
      alert("Por favor, preencha todos os campos.");
      return;
    }

    const novoConsumo: Consumo = {
      idPedido: (consumos.length + 1).toString(),
      cliente: cliente,
      servico: servico,
      produto: produto,
      valor: precoServico,
    };
    setConsumos([...consumos, novoConsumo]); // Adiciona o novo consumo à lista
    setCliente(""); // Limpa o campo Cliente
    setServico(""); // Limpa o campo Serviço
    setProduto(""); // Limpa o campo Produto
    setPrecoServico(""); // Limpa o campo Preço
  };

  return (
    <div>
      {/* Menu de Navegação */}
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="#!">
            Cadastros
          </a>
          <a style={botaoStyle} href="#!">
            Cliente
          </a>
          <a style={botaoStyle} href="#!">
            Produto
          </a>
          <a style={botaoStyle} href="#!">
            Consumo
          </a>
          <a style={botaoStyle} href="#!">
            Serviços
          </a>
          <a style={botaoStyle} href="#!">
            Listagens
          </a>
        </div>
      </nav>

      {/* Formulário de Cadastro */}
      <div className="container">
        <h4 className="center-align">Cadastrar Consumo</h4>
        <form className="col s12">
          <div className="row">
            <div className="input-field col s12">
              <select value={cliente} onChange={(e) => setCliente(e.target.value)}>
                <option value="">Selecione o Cliente</option>
                <option value="João Silva">João Silva</option>
                <option value="Maria Souza">Maria Souza</option>
                <option value="Ana Oliveira">Ana Oliveira</option>
              </select>
              <label>Cliente</label>
            </div>

            <div className="input-field col s12">
              <select value={servico} onChange={(e) => setServico(e.target.value)}>
                <option value="">Selecione o Serviço</option>
                <option value="Limpeza de Pele">Limpeza de Pele</option>
                <option value="Hidratação Facial">Hidratação Facial</option>
                <option value="Maquiagem Profissional">Maquiagem Profissional</option>
              </select>
              <label>Serviço</label>
            </div>

            <div className="input-field col s12">
              <select value={produto} onChange={(e) => setProduto(e.target.value)}>
                <option value="">Selecione o Produto</option>
                <option value="Shampoo Hidratante">Shampoo Hidratante</option>
                <option value="Máscara de Tratamento">Máscara de Tratamento</option>
                <option value="Esfoliante Corporal">Esfoliante Corporal</option>
              </select>
              <label>Produto</label>
            </div>

            <div className="input-field col s12">
              <input
                type="number"
                id="precoServico"
                value={precoServico}
                onChange={(e) => setPrecoServico(e.target.value)}
                step="0.01"
                min="0"
                required
              />
              <label htmlFor="precoServico">Preço do Serviço (R$)</label>
            </div>
          </div>
          <button
            type="button"
            onClick={handleRegister}
            style={botaoStyle}
          >
            Registrar
          </button>
        </form>
      </div>

      {/* Lista de Consumo */}
      <div className="container">
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Cliente</th>
              <th>Serviço</th>
              <th>Produto</th>
              <th>Valor</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.idPedido}</td>
                  <td>{consumo.cliente}</td>
                  <td>{consumo.servico}</td>
                  <td>{consumo.produto}</td>
                  <td>{consumo.valor}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Sem registros de consumo.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
