import { Component } from "react";
import CSS from "csstype";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

export default class CadastroCliente extends Component {
  state = {
    nome: "",
    cpf: "",
    rg: "",
    dataEmissao: "",
    telefone: "",
    dataCadastro: "",
  };

  handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as any);
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro } = this.state;

    const clienteNovo = {
      id: String(Date.now()), // Gerar ID único baseado no timestamp
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
    };

    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    clientes.push(clienteNovo);
    localStorage.setItem("clientes", JSON.stringify(clientes));

    this.setState({
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
    });

    alert("Cliente cadastrado com sucesso!");
  };

  render() {
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro } = this.state;

    return (
      <div className="container">
        <h5>Cadastro de Cliente</h5>
        <form onSubmit={this.handleSubmit}>
          <label>Nome:</label>
          <input type="text" name="nome" value={nome} onChange={this.handleChange} required />
          <label>CPF:</label>
          <input type="text" name="cpf" value={cpf} onChange={this.handleChange} required />
          <label>RG:</label>
          <input type="text" name="rg" value={rg} onChange={this.handleChange} required />
          <label>Data Emissão:</label>
          <input type="date" name="dataEmissao" value={dataEmissao} onChange={this.handleChange} required />
          <label>Telefone:</label>
          <input type="text" name="telefone" value={telefone} onChange={this.handleChange} required />
          <label>Data Cadastro:</label>
          <input type="date" name="dataCadastro" value={dataCadastro} onChange={this.handleChange} required />
          <button type="submit" style={botaoStyle}>Registrar Cliente</button>
        </form>
      </div>
    );
  }
}
