import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Produto = {
  nome: string;
  valor: string;
  descricao: string;  // Nova propriedade
  quantidade: number; // Nova propriedade
};

type Props = {
  tema?: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const ListaProdutos: React.FC<Props> = () => {
  const [produtos, setProdutos] = useState<Produto[]>(JSON.parse(localStorage.getItem('produtos') || '[]'));
  const [isEditing, setIsEditing] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number>(-1);
  const [editNome, setEditNome] = useState<string>('');
  const [editPreco, setEditPreco] = useState<string>('');
  const [editDescricao, setEditDescricao] = useState<string>('');  // Estado para descrição
  const [editQuantidade, setEditQuantidade] = useState<number>(0);  // Estado para quantidade

  const handleEdit = (index: number) => {
    const produto = produtos[index];
    setIsEditing(true);
    setEditIndex(index);
    setEditNome(produto.nome);
    setEditPreco(produto.valor);
    setEditDescricao(produto.descricao);  // Carregar a descrição
    setEditQuantidade(produto.quantidade);  // Carregar a quantidade
  };

  const handleSaveEdit = () => {
    const updatedProdutos = [...produtos];
    updatedProdutos[editIndex] = {
      nome: editNome,
      valor: editPreco,
      descricao: editDescricao,  // Atualizar a descrição
      quantidade: editQuantidade,  // Atualizar a quantidade
    };
    localStorage.setItem('produtos', JSON.stringify(updatedProdutos));
    setProdutos(updatedProdutos);
    setIsEditing(false);
    setEditIndex(-1);
    setEditNome('');
    setEditPreco('');
    setEditDescricao('');
    setEditQuantidade(0);
  };

  const handleDelete = (index: number) => {
    const updatedProdutos = [...produtos];
    updatedProdutos.splice(index, 1);
    localStorage.setItem('produtos', JSON.stringify(updatedProdutos));
    setProdutos(updatedProdutos);
    alert('Produto deletado com sucesso!');
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    if (name === 'nome') {
      setEditNome(value);
    } else if (name === 'preco') {
      setEditPreco(value);
    } else if (name === 'descricao') {
      setEditDescricao(value);
    } else if (name === 'quantidade') {
      setEditQuantidade(Number(value));
    }
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>World Beauty</a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Produtos</h5>
        {isEditing && (
          <div>
            <h6>Editar Produto</h6>
            <input
              type="text"
              name="nome"
              value={editNome}
              onChange={handleInputChange}
              placeholder="Nome do Produto"
            />
            <input
              type="text"
              name="preco"
              value={editPreco}
              onChange={handleInputChange}
              placeholder="Preço do Produto"
            />
            <input
              type="text"
              name="descricao"
              value={editDescricao}
              onChange={handleInputChange}
              placeholder="Descrição do Produto"
            />
            <input
              type="number"
              name="quantidade"
              value={editQuantidade}
              onChange={handleInputChange}
              placeholder="Quantidade"
            />
            <button onClick={handleSaveEdit} style={actionButtonStyle}>Salvar</button>
            <button onClick={() => setIsEditing(false)} style={actionButtonStyle}>Cancelar</button>
          </div>
        )}
        <table className="striped centered">
          <thead>
            <tr>
              <th>Produto</th>
              <th>Preço</th>
              <th>Descrição</th> {/* Nova coluna */}
              <th>Quantidade</th> {/* Nova coluna */}
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.length > 0 ? (
              produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.nome}</td>
                  <td>{produto.valor}</td>
                  <td>{produto.descricao}</td> {/* Exibindo descrição */}
                  <td>{produto.quantidade}</td> {/* Exibindo quantidade */}
                  <td style={{ textAlign: "right" }}>
                    <button onClick={() => handleEdit(index)} style={actionButtonStyle}>Editar</button>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Nenhum produto cadastrado</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaProdutos;
