import React, { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

type Pedido = {
  id: number;
  nomeProduto: string;
  valor: number;
  nomeServico: string;
  idProduto: number;
  idServico: number;
};

type Props = {
  onCadastroSubmit: (cliente: string, servico: string, produto: string, preco: number) => void;
  pedidos: Pedido[]; // Adicionando a propriedade pedidos
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const FormularioCadastroConsumo: React.FC<Props> = ({ onCadastroSubmit, pedidos }) => {
  const [cliente, setCliente] = useState("");
  const [servico, setServico] = useState("");
  const [produto, setProduto] = useState("");
  const [precoServico, setPrecoServico] = useState(0);

  useEffect(() => {
    document.addEventListener("DOMContentLoaded", function () {
      const elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }, []);

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();

    if (!cliente || !servico || !produto || precoServico <= 0) {
      alert("Por favor, preencha todos os campos corretamente.");
      return;
    }

    onCadastroSubmit(cliente, servico, produto, precoServico);

    setCliente("");
    setServico("");
    setProduto("");
    setPrecoServico(0);
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <br />
      <div className="container">
        <h4 className="center-align">Cadastrar Consumo</h4>
        <form className="col s12 formCli" onSubmit={handleSubmit}>
          <div className="row">
            <div className="input-field col s12">
              <select value={cliente} onChange={(e) => setCliente(e.target.value)}>
                <option value="">Selecione o Cliente</option>
                <option value="1">João Silva</option>
                <option value="2">Maria Souza</option>
                <option value="3">Ana Oliveira</option>
                <option value="4">Carlos Pereira</option>
                <option value="5">Camila Santos</option>
                <option value="6">Paulo Costa</option>
                <option value="7">Lara Fernandes</option>
                <option value="8">Juliana Lima</option>
                <option value="9">Fábio Rocha</option>
                <option value="10">Roberta Alves</option>
              </select>
              <label>Cliente</label>
            </div>

            <div className="input-field col s12">
              <select value={servico} onChange={(e) => setServico(e.target.value)}>
                <option value="">Selecione o Serviço</option>
                <option value="1">Limpeza de Pele</option>
                <option value="2">Hidratação Facial</option>
                <option value="3">Maquiagem Profissional</option>
                <option value="4">Depilação a Cera</option>
                <option value="5">Massagem Relaxante</option>
                <option value="6">Penteado para Festa</option>
                <option value="7">Tratamento Capilar</option>
                <option value="8">Manicure e Pedicure</option>
                <option value="9">Design de Sobrancelhas</option>
                <option value="10">Cuidados com a Pele</option>
              </select>
              <label>Serviço</label>
            </div>

            <div className="input-field col s12">
              <select value={produto} onChange={(e) => setProduto(e.target.value)}>
                <option value="">Selecione o Produto</option>
                <option value="1">Shampoo Hidratante</option>
                <option value="2">Creme de Hidratação Intensiva</option>
                <option value="3">Máscara de Tratamento Capilar</option>
                <option value="4">Esfoliante Corporal</option>
                <option value="5">Base Líquida</option>
                <option value="6">Creme Anti-idade</option>
                <option value="7">Perfume Floral</option>
                <option value="8">Protetor Solar Facial</option>
                <option value="9">Kit Manicure</option>
                <option value="10">Óleo Essencial</option>
              </select>
              <label>Produto</label>
            </div>

            <div className="input-field col s12">
              <input
                type="number"
                value={precoServico}
                onChange={(e) => setPrecoServico(parseFloat(e.target.value))}
                min="0"
                step="0.01"
                required
              />
              <label htmlFor="precoServico">Preço do Serviço (R$)</label>
            </div>
          </div>

          <div className="row">
            <div className="col s12">
              <button style={botaoStyle} type="submit">
                Registrar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormularioCadastroConsumo;
