import React, { useState } from "react";
import ListaPedidos from "./ListaPedidos";

const App: React.FC = () => {
  // Estado de exemplo com dados fictícios de pedidos
  const [pedidos, setPedidos] = useState([
    {
      id: 1,
      nomeProduto: "Produto A",
      valor: 150,
      nomeServico: "Serviço 1",
      idProduto: 1,
      idServico: 2,
    },
    {
      id: 2,
      nomeProduto: "Produto B",
      valor: 200,
      nomeServico: "Serviço 2",
      idProduto: 2,
      idServico: 3,
    },
  ]);

  // Função para editar um pedido
  const handleEditar = (id: number) => {
    console.log("Editar pedido com id:", id);
    // Lógica para editar o pedido
  };

  // Função para excluir um pedido
  const handleExcluir = (id: number) => {
    console.log("Excluir pedido com id:", id);
    // Lógica para excluir o pedido
    setPedidos(pedidos.filter((pedido) => pedido.id !== id));
  };

  return (
    <div>
      <h1>Gestão de Pedidos</h1>
      {/* Passando as props para ListaPedidos */}
      <ListaPedidos
        pedidos={pedidos}
        onEditar={handleEditar}
        onExcluir={handleExcluir}
      />
    </div>
  );
};

export default App;
