import { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

class ListaProduto extends Component {
  state = {
    produtos: [] as { nomeProduto: string; preco: string }[],
  };

  componentDidMount() {
    // Recupera os produtos do localStorage quando o componente for montado
    const storedProducts = JSON.parse(localStorage.getItem("produtos") || "[]");

    // Se não houver produtos, adiciona 5 produtos cosméticos fictícios
    if (storedProducts.length === 0) {
      const produtosFicticios = [
        { nomeProduto: "Creme Hidratante", preco: "R$ 45,90" },
        { nomeProduto: "Shampoo Revitalizante", preco: "R$ 29,99" },
        { nomeProduto: "Máscara Facial Anti-Aging", preco: "R$ 69,90" },
        { nomeProduto: "Protetor Solar FPS 50", preco: "R$ 34,90" },
        { nomeProduto: "Esfoliante Corporal", preco: "R$ 39,90" },
      ];
      // Armazena os produtos fictícios no localStorage
      localStorage.setItem("produtos", JSON.stringify(produtosFicticios));
      this.setState({ produtos: produtosFicticios });
    } else {
      this.setState({ produtos: storedProducts });
    }
  }

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">Serviços</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Lista de Produtos</h5>
          <table className="striped centered">
            <thead>
              <tr>
                <th>Produto</th>
                <th>Preço</th>
              </tr>
            </thead>
            <tbody>
              {this.state.produtos.map((produto, index) => (
                <tr key={index}>
                  <td>{produto.nomeProduto}</td>
                  <td>{produto.preco}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaProduto;
