import { useState } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

export default function FormularioCadastroServico() {
  const [nome, setNome] = useState("");
  const [valor, setValor] = useState("");
  const [descricao, setDescricao] = useState("");

  const handleRegistrar = () => {
    const servico = { nome, valor, descricao };
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    servicos.push(servico);
    localStorage.setItem("servicos", JSON.stringify(servicos));
    setNome("");
    setValor("");
    setDescricao("");
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>World Beauty</a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Servi√ßos</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Cadastrar Novo Servi√ßo</h5>
        <div className="row">
          <form className="col s12">
            <div className="row">
              <div className="input-field col s6">
                <input id="nome_servico" type="text" value={nome} onChange={(e) => setNome(e.target.value)} />
                <label htmlFor="nome_servico">Nome do Servi√ßo</label>
              </div>
              <div className="input-field col s6">
                <input id="valor_servico" type="text" value={valor} onChange={(e) => setValor(e.target.value)} />
                <label htmlFor="valor_servico">Valor do Servi√ßo</label>
              </div>
              <div className="input-field col s12">
                <input id="descricao_servico" type="text" value={descricao} onChange={(e) => setDescricao(e.target.value)} />
                <label htmlFor="descricao_servico">Descri√ß√£o do Servi√ßo</label>
              </div>
            </div>
            <button style={botaoStyle} type="button" onClick={handleRegistrar}>Registrar</button>
          </form>
        </div>
      </div>
    </div>
  );
}
