import React from "react";
import CSS from "csstype";

type Pedido = {
  idPedido: number;
  idProduto: number;
  nomeProduto: string;
  valorProduto: number;
  idServico: number;
  nomeServico: string;
  valorServico: number;
};

type Props = {
  pedidos: Pedido[]; // A lista de pedidos passada como props
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

const Pedidos: React.FC<Props> = ({ pedidos }) => {
  // Função para exportar em formato CSV
  const exportCSV = () => {
    const header = ['Id_Pedido', 'Id_Produto', 'Nome do Produto', 'Valor Produto', 'Id_Servico', 'Nome do Serviço', 'Valor Serviço'];
    const rows = pedidos.map(pedido => [
      pedido.idPedido,
      pedido.idProduto,
      pedido.nomeProduto,
      pedido.valorProduto,
      pedido.idServico,
      pedido.nomeServico,
      pedido.valorServico
    ]);

    let csvContent = "data:text/csv;charset=utf-8," + header.join(",") + "\n";
    rows.forEach(row => {
      csvContent += row.join(",") + "\n";
    });

    // Criando o link de download e iniciando o download
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "pedidos.csv");
    document.body.appendChild(link); // Requerido para o Firefox
    link.click();
  };

  // Função para exportar em formato JSON
  const exportJSON = () => {
    const jsonContent = JSON.stringify(pedidos, null, 2);

    const blob = new Blob([jsonContent], { type: 'application/json' });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "pedidos.json";
    link.click();
  };

  return (
    <div>
      <nav className="">
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>

          <a style={botaoStyle} href="/cadServico">
            Cadastros
          </a>
          <a style={botaoStyle} href="/listaCliente">
            Cliente
          </a>
          <a style={botaoStyle} href="/listaProduto">
            Produto
          </a>
          <a style={botaoStyle} href="/listaPedidos">
            Consumo
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Serviços
          </a>
          <a style={botaoStyle} href="/Listagens">
            Listagens
          </a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Pedidos</h5>

        <button style={botaoStyle} onClick={exportCSV}>Exportar CSV</button>
        <button style={botaoStyle} onClick={exportJSON}>Exportar JSON</button>

        <br />
        <h5 className="center-align">Produto</h5>
        <table className="highlight centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map(pedido => (
              <tr key={pedido.idPedido}>
                <td>{pedido.idPedido}</td>
                <td>{pedido.idProduto}</td>
                <td>{pedido.nomeProduto}</td>
                <td>{pedido.valorProduto}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <br />
        <h5 className="center-align">Serviço</h5>
        <table className="highlight centered">
          <thead>
            <tr>
              <th>Id_Servico</th>
              <th>Id_Produto</th>
              <th>Nome do Serviço</th>
              <th>Valor</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map(pedido => (
              <tr key={pedido.idPedido}>
                <td>{pedido.idServico}</td>
                <td>{pedido.idProduto}</td>
                <td>{pedido.nomeServico}</td>
                <td>{pedido.valorServico}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Pedidos;
