// ListaConsumo.tsx
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const location = useLocation();
  const [consumos, setConsumos] = useState<Consumo[]>([]);

  useEffect(() => {
    if (location.state && (location.state as any).novoConsumo) {
      const novoConsumo = (location.state as any).novoConsumo as Consumo;
      setConsumos((prevConsumos) => [...prevConsumos, novoConsumo]);
    }
  }, [location.state]);

  const handleDelete = (index: number) => {
    const updatedConsumos = [...consumos];
    updatedConsumos.splice(index, 1);
    setConsumos(updatedConsumos);
    alert("Consumo deletado com sucesso!");
  };

  return (
    <div className="container">
      <h5 className="center-align">Lista de Consumo</h5>

      <table className="striped centered">
        <thead>
          <tr>
            <th>Id_Pedido</th>
            <th>Id_Produto</th>
            <th>Nome do Produto</th>
            <th>Valor</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {consumos.length > 0 ? (
            consumos.map((consumo, index) => (
              <tr key={index}>
                <td>{consumo.idPedido}</td>
                <td>{consumo.idProduto}</td>
                <td>{consumo.nomeProduto}</td>
                <td>{consumo.valor}</td>
                <td>
                  <button onClick={() => handleDelete(index)}>Deletar</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={5}>Nenhum consumo registrado!</td>
            </tr>
          )}
        </tbody>
      </table>
      <br />
    </div>
  );
};

export default ListaConsumo;
