import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";

// Estilos existentes mantidos...
const estiloContainer = {
  display: "flex",
};

const estiloNav = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const estiloLogo = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const estiloMenuLinks = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const estiloBotao = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

// Interfaces para os diferentes tipos de dados
interface ClienteValor {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  valorConsumido: number;
}

interface ClienteQuantidade {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  quantidadeConsumida: number;
}

interface ProdutoServico {
  id: number;
  nome: string;
  valor: number;
  tipo: string;
}

interface ConsumoGenero {
  id: number;
  nomeCliente: string;
  produtoServico: string;
  valor: number;
}

const Listagens = () => {
  // Estados para dados de exemplo
  const [clientesValor, setClientesValor] = useState<ClienteValor[]>([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
  ]);

  const [clientesQuantidade, setClientesQuantidade] = useState<ClienteQuantidade[]>([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", quantidadeConsumida: 5 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", quantidadeConsumida: 3 },
  ]);

  const [produtosServicos, setProdutosServicos] = useState<ProdutoServico[]>([
    { id: 1, nome: "Corte de Cabelo", valor: 50.00, tipo: "Serviço" },
    { id: 2, nome: "Shampoo", valor: 25.00, tipo: "Produto" },
  ]);

  const [consumoGenero, setConsumoGenero] = useState<ConsumoGenero[]>([
    { id: 1, nomeCliente: "Ana", produtoServico: "Manicure", valor: 35.00 },
    { id: 2, nomeCliente: "João", produtoServico: "Barba", valor: 25.00 },
  ]);

  // Estados para controle de edição
  const [itemEditando, setItemEditando] = useState<any>(null);
  const [secaoEditando, setSecaoEditando] = useState<number | null>(null);
  const [dadosFormularioEdicao, setDadosFormularioEdicao] = useState<any>(null);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  // Manipulação de edição
  const manipularEdicao = (item: any, secao: number) => {
    setItemEditando(item);
    setSecaoEditando(secao);
    setDadosFormularioEdicao(item);
  };

  const manipularMudancaInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setDadosFormularioEdicao((prev: any) => ({
      ...prev,
      [name]: value,
    }));
  };

  const manipularEnvioFormulario = (e: React.FormEvent) => {
    e.preventDefault();

