import React, { Component } from "react";
import CSS from "csstype";
import editar from "../Assets/edit.png"; // Imagem de editar
import excluir from "../Assets/delete.png"; // Imagem de excluir

type Props = {
  tema: string;
};

type Consumo = {
  id: number;
  nomeProduto: string;
  valor: number;
  nomeServico: string;
  idProduto: number;
  idServico: number;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "Arial, sans-serif",
  color: "#fff", // Texto branco
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

export default class Consumos extends Component<any, Props> {
  // Dados de exemplo para os consumos
  consumo: Consumo[] = [
    {
      id: 1,
      nomeProduto: "Produto A",
      valor: 100.0,
      nomeServico: "Serviço X",
      idProduto: 101,
      idServico: 201,
    },
    {
      id: 2,
      nomeProduto: "Produto B",
      valor: 150.0,
      nomeServico: "Serviço Y",
      idProduto: 102,
      idServico: 202,
    },
  ];

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaConsumos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              Serviços
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>
        <div className="container">
          {/* Tabela de Consumos */}
          <h5 className="center-align">Consumos</h5>
          <table className="highlight centered">
            <thead>
              <tr>
                <th>Id_Consumo</th>
                <th>Nome Produto</th>
                <th>Valor</th>
                <th>Nome Serviço</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {this.consumo.map((consumo) => (
                <tr key={consumo.id}>
                  <td>{consumo.id}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td>{consumo.nomeServico}</td>
                  <td>
                    <img
                      src={editar}
                      alt="Editar"
                      style={ImagemStyle}
                      onClick={() => this.editarConsumo(consumo.id)}
                    />
                    <img
                      src={excluir}
                      alt="Excluir"
                      style={ImagemStyle}
                      onClick={() => this.excluirConsumo(consumo.id)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }

  // Função para editar um consumo
  editarConsumo(id: number) {
    console.log(`Editar consumo com id: ${id}`);
    // Adicione a lógica de navegação ou manipulação aqui
  }

  // Função para excluir um consumo
  excluirConsumo(id: number) {
    console.log(`Excluir consumo com id: ${id}`);
    // Adicione a lógica para excluir o consumo aqui
  }
}
