import React, { useState } from "react";
import CadastroConsumo from "./consumo"; // Importa o componente CadastroConsumo

export type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([
    {
      idPedido: "001",
      idProduto: "P001",
      nomeProduto: "Produto 1",
      valor: "100,00",
    },
    {
      idPedido: "002",
      idProduto: "P002",
      nomeProduto: "Produto 2",
      valor: "150,00",
    },
  ]);

  const addConsumo = (novoConsumo: Consumo) => {
    setConsumos((prevConsumos) => [...prevConsumos, novoConsumo]);
  };

  const handleEdit = (index: number) => {
    // Função de edição
  };

  const handleDelete = (index: number) => {
    // Função de deletação
  };

  return (
    <div>
      {/* Menu de Navegação */}
      <nav>
        {/*... Seu código de navegação...*/}
      </nav>

      {/* Formulário de Cadastro */}
      <CadastroConsumo addConsumo={addConsumo} />

      {/* Lista de Consumo */}
      <div className="container">
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.idPedido}</td>
                  <td>{consumo.idProduto}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td style={{ textAlign: "right" }}>
                    <button onClick={() => handleEdit(index)}>Editar</button>
                    <button onClick={() => handleDelete(index)}>Deletar</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Sem registros de consumo.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
