import React, { Component } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#49786d",
};
const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#49786d",
};

const botaoColor: CSS.Properties = {
  backgroundColor: "#49786d",
  fontFamily: "arial",
};

class PaginaInicial extends Component<any, any> {
  state = {
    activeTab: "cliente", // Controle da aba ativa
  };

  // FunÃ§Ã£o para mudar a aba ativa
  setActiveTab = (tab: string) => {
    this.setState({ activeTab: tab });
  };

  // FunÃ§Ã£o para ir diretamente para a pÃ¡gina de clientes ao clicar no botÃ£o "ENTRAR"
  handleEntrar = () => {
    window.location.href = "/listaCliente"; // Redireciona para a pÃ¡gina de clientes
  };

  render() {
    const { activeTab } = this.state;
    return (
      <div>
        <nav className="" style={backgroundColor}>
          <div className="nav-wrapper">
            <a className="brand-logo center" style={fontStyle}>
              World Beauty
            </a>
          </div>
        </nav>
        <br />
        <div className="container">
          <div className="center-align">
            {/* Abas de navegaÃ§Ã£o */}
            <div className="tabs">
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "cliente" ? "active" : ""}`}
                onClick={() => this.setActiveTab("cliente")}
              >
                Cadastrar Clientes
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "produto" ? "active" : ""}`}
                onClick={() => this.setActiveTab("produto")}
              >
                Cadastrar Produtos
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "servico" ? "active" : ""}`}
                onClick={() => this.setActiveTab("servico")}
              >
                Cadastrar ServiÃ§os
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "pedido" ? "active" : ""}`}
                onClick={() => this.setActiveTab("pedido")}
              >
                Cadastrar Consumo
              </button>
            </div>

            {/* ConteÃºdo das Abas */}
            <div className="tab-content">
              {activeTab === "cliente" && (
                <div>
                  <h3>Cadastrar Cliente</h3>
                  <p>Aqui vocÃª pode cadastrar um cliente.</p>
                  <a href="/formularioCadastroCliente" className="waves-effect waves-light btn">
                    Acessar Cadastro de Clientes
                  </a>
                  <br />
                  <button onClick={this.handleGoBack} className="waves-effect waves-light btn" style={botaoColor}>
                    Voltar
                  </button>
                </div>
              )}
              {activeTab === "produto" && (
                <div>
                  <h3>Cadastrar Produto</h3>
                  <p>Aqui vocÃª pode cadastrar um produto.</p>
                  <a href="/formularioCadastroProduto" className="waves-effect waves-light btn">
                    Acessar Cadastro de Produtos
                  </a>
                  <br />
                  <button onClick={this.handleGoBack} className="waves-effect waves-light btn" style={botaoColor}>
                    Voltar
                  </button>
                </div>
              )}
              {activeTab === "servico" && (
                <div>
                  <h3>Cadastrar ServiÃ§o</h3>
                  <p>Aqui vocÃª pode cadastrar um serviÃ§o.</p>
                  <a href="/formularioCadastroServico" className="waves-effect waves-light btn">
                    Acessar Cadastro de ServiÃ§os
                  </a>
                  <br />
                  <button onClick={this.handleGoBack} className="waves-effect waves-light btn" style={botaoColor}>
                    Voltar
                  </button>
                </div>
              )}
              {activeTab === "pedido" && (
                <div>
                  <h3>Cadastrar Consumo</h3>
                  <p>Aqui vocÃª pode cadastrar um consumo.</p>
                  <a href="/formularioCadastroPedido" className="waves-effect waves-light btn">
                    Acessar Cadastro de Consumo
                  </a>
                  <br />
                  <button onClick={this.handleGoBack} className="waves-effect waves-light btn" style={botaoColor}>
                    Voltar
                  </button>
                </div>
              )}
            </div>

            {/* BotÃ£o Entrar para a pÃ¡gina de clientes */}
            <br />
            <button
              onClick={this.handleEntrar}
              className="waves-effect waves-light btn"
              style={botaoStyle}
            >
              ENTRAR
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default PaginaInicial;
