import React, { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

// Definindo as cores e estilos
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

// Tipando o tipo de consumo
interface Consumo {
  cliente: string;
  servico: string;
  produto: string;
}

const FormularioCadastroConsumo: React.FC = () => {
  // Tipando o estado como string
  const [cliente, setCliente] = useState<string>("");
  const [servico, setServico] = useState<string>("");
  const [produto, setProduto] = useState<string>("");

  // Função para lidar com as mudanças nos selects
  const handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name === "cliente") {
      setCliente(value);
    } else if (name === "servico") {
      setServico(value);
    } else if (name === "produto") {
      setProduto(value);
    }
  };

  // Função de envio do formulário
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const newConsumo: Consumo = { cliente, servico, produto };
    const storedConsumos: Consumo[] = JSON.parse(localStorage.getItem("consumos") || "[]");

    storedConsumos.push(newConsumo);
    localStorage.setItem("consumos", JSON.stringify(storedConsumos));

    console.log("Consumo registrado:", newConsumo);

    // Limpando os campos após o envio
    setCliente("");
    setServico("");
    setProduto("");
  };

  // Inicializando os selects com Materialize após o carregamento da página
  useEffect(() => {
    document.addEventListener("DOMContentLoaded", function () {
      var elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }, []);

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <br />
      <div className="container">
        <h4 className="center-align">Cadastrar Consumo</h4>
        <form className="col s12" onSubmit={handleSubmit}>
          <div className="row">
            <div className="input-field col s12 opcoes">
              <select name="cliente" value={cliente} onChange={handleChange}>
                <option value="">Selecione o Cliente</option>
                <option value="1">Cliente - João Silva</option>
                <option value="2">Cliente - Maria Souza</option>
                <option value="3">Cliente - Ana Oliveira</option>
                <option value="4">Cliente - Carlos Pereira</option>
                <option value="5">Cliente - Camila Santos</option>
                <option value="6">Cliente - Paulo Costa</option>
                <option value="7">Cliente - Lara Fernandes</option>
                <option value="8">Cliente - Juliana Lima</option>
                <option value="9">Cliente - Fábio Rocha</option>
                <option value="10">Cliente - Roberta Alves</option>
              </select>
            </div>

            <div className="input-field col s12 opcoes">
              <select name="servico" value={servico} onChange={handleChange}>
                <option value="">Selecione o Serviço</option>
                <option value="1">Limpeza de Pele</option>
                <option value="2">Hidratação Facial</option>
                <option value="3">Maquiagem Profissional</option>
                <option value="4">Depilação a Cera</option>
                <option value="5">Massagem Relaxante</option>
                <option value="6">Penteado para Festa</option>
                <option value="7">Tratamento Capilar</option>
                <option value="8">Manicure e Pedicure</option>
                <option value="9">Design de Sobrancelhas</option>
                <option value="10">Cuidados com a Pele</option>
              </select>
            </div>

            <div className="input-field col s12 opcoes">
              <select name="produto" value={produto} onChange={handleChange}>
                <option value="">Selecione o Produto</option>
                <option value="1">Shampoo Hidratante</option>
                <option value="2">Creme de Hidratação Intensiva</option>
                <option value="3">Máscara de Tratamento Capilar</option>
                <option value="4">Esfoliante Corporal</option>
                <option value="5">Base Líquida</option>
                <option value="6">Creme Anti-idade</option>
                <option value="7">Perfume Floral</option>
                <option value="8">Protetor Solar Facial</option>
                <option value="9">Kit Manicure</option>
                <option value="10">Óleo Essencial</option>
              </select>
            </div>
          </div>

          <div className="row">
            <div className="col s12">
              <button style={botaoStyle} type="submit" name="action">
                Registrar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormularioCadastroConsumo;
