import React, { useState } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

// Estilos CSS
const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const CadastroProduto: React.FC = () => {
  const [idProduto, setIdProduto] = useState("");
  const [nomeProduto, setNomeProduto] = useState("");
  const [valor, setValor] = useState("");

  const handleCadastro = () => {
    const novoProduto = {
      idProduto,
      nomeProduto,
      valor,
    };

    // Recupera produtos existentes no localStorage ou cria uma lista vazia
    const produtosExistentes = JSON.parse(localStorage.getItem("produtos") || "[]");

    // Adiciona o novo produto e armazena no localStorage
    produtosExistentes.push(novoProduto);
    localStorage.setItem("produtos", JSON.stringify(produtosExistentes));

    // Limpa os campos
    setIdProduto("");
    setNomeProduto("");
    setValor("");

    alert("Produto cadastrado com sucesso!");
  };

  return (
    <div className="container">
      <h4 className="center-align">Cadastrar Produto</h4>
      <div className="input-field">
        <input
          type="text"
          value={idProduto}
          onChange={(e) => setIdProduto(e.target.value)}
          placeholder="ID do Produto"
          required
        />
      </div>
      <div className="input-field">
        <input
          type="text"
          value={nomeProduto}
          onChange={(e) => setNomeProduto(e.target.value)}
          placeholder="Nome do Produto"
          required
        />
      </div>
      <div className="input-field">
        <input
          type="text"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          placeholder="Valor"
          required
        />
      </div>
      <button style={botaoStyle} onClick={handleCadastro}>
        Cadastrar Produto
      </button>
    </div>
  );
};

export default CadastroProduto;
