import React, { Component } from "react";
import CSS from "csstype";

// Estilos para a página
const backgroundColor: CSS.Properties = {
  backgroundColor: "#49786d",
};
const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#49786d",
};

const botaoColor: CSS.Properties = {
  backgroundColor: "#49786d",
  fontFamily: "arial",
};

// Componente principal com abas
class PaginaInicial extends Component<any, any> {
  state = {
    activeTab: "cliente", // Controle da aba ativa
  };

  // Função para mudar a aba ativa
  setActiveTab = (tab: string) => {
    this.setState({ activeTab: tab });
  };

  render() {
    const { activeTab } = this.state;
    return (
      <div>
        <nav className="" style={backgroundColor}>
          <div className="nav-wrapper">
            <a className="brand-logo center" style={fontStyle}>
              World Beauty
            </a>
          </div>
        </nav>
        <br />
        <div className="container">
          <div className="center-align">
            {/* Abas de navegação */}
            <div className="tabs">
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "cliente" ? "active" : ""}`}
                onClick={() => this.setActiveTab("cliente")}
              >
                Cadastrar Clientes
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "produto" ? "active" : ""}`}
                onClick={() => this.setActiveTab("produto")}
              >
                Cadastrar Produtos
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "servico" ? "active" : ""}`}
                onClick={() => this.setActiveTab("servico")}
              >
                Cadastrar Serviços
              </button>
              <button
                style={botaoStyle}
                className={`tab-button ${activeTab === "pedido" ? "active" : ""}`}
                onClick={() => this.setActiveTab("pedido")}
              >
                Cadastrar Consumo
              </button>
            </div>

            {/* Conteúdo das Abas */}
            <div className="tab-content">
              {activeTab === "cliente" && (
                <div>
                  <h3>Cadastrar Cliente</h3>
                  <p>Aqui você pode cadastrar um cliente.</p>
                  <a href="/formularioCadastroCliente" className="waves-effect waves-light btn">
                    Acessar Cadastro de Clientes
                  </a>
                </div>
              )}
              {activeTab === "produto" && (
                <div>
                  <h3>Cadastrar Produto</h3>
                  <p>Aqui você pode cadastrar um produto.</p>
                  <a href="/formularioCadastroProduto" className="waves-effect waves-light btn">
                    Acessar Cadastro de Produtos
                  </a>
                </div>
              )}
              {activeTab === "servico" && (
                <div>
                  <h3>Cadastrar Serviço</h3>
                  <p>Aqui você pode cadastrar um serviço.</p>
                  <a href="/formularioCadastroServico" className="waves-effect waves-light btn">
                    Acessar Cadastro de Serviços
                  </a>
                </div>
              )}
              {activeTab === "pedido" && (
                <div>
                  <h3>Cadastrar Consumo</h3>
                  <p>Aqui você pode cadastrar um consumo.</p>
                  <a href="/formularioCadastroPedido" className="waves-effect waves-light btn">
                    Acessar Cadastro de Consumo
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default PaginaInicial;
