import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
  dataNascimento: string;
};

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "250px", // Largura do menu
  backgroundColor: "#000",
  padding: "20px",
  height: "100vh", // Altura para preencher a tela
  display: "flex",
  flexDirection: "column", // Organizar itens na vertical
  justifyContent: "flex-start", // Alinhar ao topo
};

const logoStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
  marginBottom: "20px", // Espaço abaixo da logo
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column", // Itens do menu em coluna
  gap: "15px",
};

const botaoStyle: CSS.Properties = {
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
  padding: "10px",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const cancelButtonStyle: CSS.Properties = {
  backgroundColor: "red", // Cor vermelha para o botão cancelar
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const ListaClientes: React.FC = () => {
  const [clientes, setClientes] = useState<Cliente[]>(JSON.parse(localStorage.getItem("clientes") || "[]"));
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(-1);
  const [editNome, setEditNome] = useState("");
  const [editCpf, setEditCpf] = useState("");
  const [editRg, setEditRg] = useState("");
  const [editDataEmissao, setEditDataEmissao] = useState("");
  const [editTelefone, setEditTelefone] = useState("");
  const [editDataCadastro, setEditDataCadastro] = useState("");
  const [editGenero, setEditGenero] = useState("");
  const [editDataNascimento, setEditDataNascimento] = useState("");

  useEffect(() => {
    const storedClientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    setClientes(storedClientes);
  }, []);

  const handleEdit = (index: number) => {
    setIsEditing(true);
    const cliente = clientes[index];
    setEditIndex(index);
    setEditNome(cliente.nome);
    setEditCpf(cliente.cpf);
    setEditRg(cliente.rg);
    setEditDataEmissao(cliente.dataEmissao);
    setEditTelefone(cliente.telefone);
    setEditDataCadastro(cliente.dataCadastro);
    setEditGenero(cliente.genero);
    setEditDataNascimento(cliente.dataNascimento);
  };

  const handleUpdate = () => {
    const updatedClientes = [...clientes];
    updatedClientes[editIndex] = {
      id: updatedClientes[editIndex].id,
      nome: editNome,
      cpf: editCpf,
      rg: editRg,
      dataEmissao: editDataEmissao,
      telefone: editTelefone,
      dataCadastro: editDataCadastro,
      genero: editGenero,
      dataNascimento: editDataNascimento,
    };

    localStorage.setItem("clientes", JSON.stringify(updatedClientes));
    setClientes(updatedClientes);
    setIsEditing(false);
    setEditIndex(-1);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditIndex(-1);
  };

  const handleDelete = (index: number) => {
    const updatedClientes = clientes.filter((_, i) => i !== index);
    localStorage.setItem("clientes", JSON.stringify(updatedClientes));
    setClientes(updatedClientes);
  };

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Lista de Clientes */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        <h5 className="center-align">Lista de Clientes</h5>
        {isEditing && (
          <div>
            <h6>Editar Cliente</h6>
            <input
              type="text"
              name="nome"
              value={editNome}
              onChange={(e) => setEditNome(e.target.value)}
              placeholder="Nome"
            />
            <input
              type="text"
              name="cpf"
              value={editCpf}
              onChange={(e) => setEditCpf(e.target.value)}
              placeholder="CPF"
            />
            <input
              type="text"
              name="rg"
              value={editRg}
              onChange={(e) => setEditRg(e.target.value)}
              placeholder="RG"
            />
            <input
              type="text"
              name="dataEmissao"
              value={editDataEmissao}
              onChange={(e) => setEditDataEmissao(e.target.value)}
              placeholder="Data Emissão"
            />
            <input
              type="text"
              name="telefone"
              value={editTelefone}
              onChange={(e) => setEditTelefone(e.target.value)}
              placeholder="Telefone"
            />
            <input
              type="text"
              name="dataCadastro"
              value={editDataCadastro}
              onChange={(e) => setEditDataCadastro(e.target.value)}
              placeholder="Data Cadastro"
            />
            <input
              type="text"
              name="genero"
              value={editGenero}
              onChange={(e) => setEditGenero(e.target.value)}
              placeholder="Gênero"
            />
            <input
              type="text"
              name="dataNascimento"
              value={editDataNascimento}
              onChange={(e) => setEditDataNascimento(e.target.value)}
              placeholder="Data Nascimento"
            />
            <button onClick={handleUpdate} style={actionButtonStyle}>Salvar</button>
            <button onClick={handleCancel} style={cancelButtonStyle}>Cancelar</button>
          </div>
        )}
        <table className="striped centered">
          <thead>
            <tr>
              <th>Nome</th>
              <th>CPF</th>
              <th>RG</th>
              <th>Data Emissão</th>
              <th>Telefone</th>
              <th>Data Cadastro</th>
              <th>Data Nascimento</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente, index) => (
              <tr key={cliente.id}>
                <td>{cliente.nome}</td>
                <td>{cliente.cpf}</td>
                <td>{cliente.rg}</td>
                <td>{cliente.dataEmissao}</td>
                <td>{cliente.telefone}</td>
                <td>{cliente.dataCadastro}</td>
                <td>{cliente.dataNascimento}</td>
                <td>
                  <button style={actionButtonStyle} onClick={() => handleEdit(index)}>Editar</button>
                  <button style={actionButtonStyle} onClick={() => handleDelete(index)}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaClientes;
