// ClienteMaiorQtd.tsx
import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";

const ClienteMaiorQtd = () => {
    const [clientesMaiorQtd, setClientesMaiorQtd] = useState<any[]>([]);
    const [editando, setEditando] = useState<boolean>(false);
    const [clienteEditado, setClienteEditado] = useState<any | null>(null);

    useEffect(() => {
        setClientesMaiorQtd([
            { id: 1, nome: "Cliente A", quantidade: 50 },
            { id: 2, nome: "Cliente B", quantidade: 120 },
            { id: 3, nome: "Cliente C", quantidade: 30 },
        ]);
    }, []);

    const handleRegistro = () => {
        const novoCliente = { id: clientesMaiorQtd.length + 1, nome: "Novo Cliente", quantidade: 10 };
        setClientesMaiorQtd([...clientesMaiorQtd, novoCliente]);
    };

    const handleEdição = (cliente: any) => {
        setEditando(true);
        setClienteEditado(cliente);
    };

    const handleDelecao = (id: number) => {
        setClientesMaiorQtd(clientesMaiorQtd.filter(cliente => cliente.id !== id));
    };

    const handleSalvarEdicao = () => {
        setClientesMaiorQtd(clientesMaiorQtd.map(cliente =>
            cliente.id === clienteEditado.id ? clienteEditado : cliente
        ));
        setEditando(false);
        setClienteEditado(null);
    };

    return (
        <div>
            <h3>Clientes com Maior Quantidade</h3>
            <Button variant="primary" onClick={handleRegistro}>Registrar Cliente</Button>
            <ul>
                {clientesMaiorQtd.map(cliente => (
                    <li key={cliente.id}>
                        {editando && cliente.id === clienteEditado?.id ? (
                            <div>
                                <input
                                    type="text"
                                    value={clienteEditado?.nome}
                                    onChange={(e) => setClienteEditado({ ...clienteEditado, nome: e.target.value })}
                                />
                                <input
                                    type="number"
                                    value={clienteEditado?.quantidade}
                                    onChange={(e) => setClienteEditado({ ...clienteEditado, quantidade: +e.target.value })}
                                />
                                <Button variant="success" onClick={handleSalvarEdicao}>Salvar</Button>
                            </div>
                        ) : (
                            <div>
                                {cliente.nome} - {cliente.quantidade} itens
                                <Button variant="info" onClick={() => handleEdição(cliente)}>Editar</Button>
                                <Button variant="danger" onClick={() => handleDelecao(cliente.id)}>Deletar</Button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClienteMaiorQtd;
