import { Component } from "react";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  dataNascimento: string;
  genero: string;
};

export default class ListaClientes extends Component {
  state = {
    clientes: [] as Cliente[],
  };

  componentDidMount() {
    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    this.setState({ clientes });
  }

  render() {
    const { clientes } = this.state;
    return (
      <div>
        <h5 className="center-align">Lista de Clientes</h5>
        <ul>
          {clientes.length > 0 ? (
            clientes.map((cliente) => (
              <li key={cliente.id}>
                <p>Nome: {cliente.nome}</p>
                <p>CPF: {cliente.cpf}</p>
                <p>RG: {cliente.rg}</p>
                <p>Data de Emissão: {cliente.dataEmissao}</p>
                <p>Telefone: {cliente.telefone}</p>
                <p>Data de Cadastro: {cliente.dataCadastro}</p>
                <p>Data de Nascimento: {cliente.dataNascimento}</p>
                <p>Gênero: {cliente.genero}</p>
              </li>
            ))
          ) : (
            <p>Nenhum cliente cadastrado.</p>
          )}
        </ul>
      </div>
    );
  }
}
