import { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Servico = {
  nome: string;
  preco: number;
};

type ListaServicosProps = {
  tema: string;
  servicos: Servico[]; // Adicionando a lista de serviços como props
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

class ListaServicos extends Component<ListaServicosProps> {
  render() {
    const { servicos } = this.props; // Pegando a lista de serviços das props

    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>

            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaPedidos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              Serviços
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>

        <br />
        <div className="container">
          <h5 className="center-align">Lista de Serviços</h5>
          <table className="striped centered">
            <thead>
              <tr>
                <th>Serviço</th>
                <th>Preço</th>
              </tr>
            </thead>
            <tbody>
              {servicos.length > 0 ? (
                servicos.map((servico, index) => (
                  <tr key={index}>
                    <td>{servico.nome}</td>
                    <td>R$ {servico.preco.toFixed(2)}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={2}>Nenhum serviço cadastrado</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaServicos;
