import { useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  // Definindo uma lista de clientes com valores de consumo
  const clientes = [
    { id: 1, nome: "Cliente 1", cpf: "000.000.000-00", telefone: "(00)00000-0000", valorConsumo: 500 },
    { id: 2, nome: "Cliente 2", cpf: "111.111.111-11", telefone: "(11)11111-1111", valorConsumo: 300 },
    { id: 3, nome: "Cliente 3", cpf: "222.222.222-22", telefone: "(22)22222-2222", valorConsumo: 450 },
    // Adicione mais clientes para completar 10
    { id: 4, nome: "Cliente 4", cpf: "333.333.333-33", telefone: "(33)33333-3333", valorConsumo: 250 },
    { id: 5, nome: "Cliente 5", cpf: "444.444.444-44", telefone: "(44)44444-4444", valorConsumo: 200 },
    { id: 6, nome: "Cliente 6", cpf: "555.555.555-55", telefone: "(55)55555-5555", valorConsumo: 100 },
    { id: 7, nome: "Cliente 7", cpf: "666.666.666-66", telefone: "(66)66666-6666", valorConsumo: 320 },
    { id: 8, nome: "Cliente 8", cpf: "777.777.777-77", telefone: "(77)77777-7777", valorConsumo: 380 },
    { id: 9, nome: "Cliente 9", cpf: "888.888.888-88", telefone: "(88)88888-8888", valorConsumo: 420 },
    { id: 10, nome: "Cliente 10", cpf: "999.999.999-99", telefone: "(99)99999-9999", valorConsumo: 600 },
  ];

  // Ordenando os clientes pelo valor de consumo em ordem decrescente
  const clientesOrdenados = clientes.sort((a, b) => b.valorConsumo - a.valorConsumo).slice(0, 10);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>
        <br />
        <div className="row">
          <div id="1" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor de Consumo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {clientesOrdenados.map((cliente) => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>R$ {cliente.valorConsumo.toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>
          {/* Outras abas seguem */}
        </div>
      </div>
    </div>
  );
};

export default Listagens;
