import React, { useState } from 'react';
import LoginRegistrationPage from './LoginRegistrationPage';
import PaginaInicial from './PaginaInicial';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Função para autenticar o usuário ao fazer login
  const onLogin = () => {
    setIsAuthenticated(true);
  };

  return (
    <div>
      {/* Exibe a página de login se o usuário não estiver autenticado, caso contrário exibe a página inicial */}
      {isAuthenticated ? (
        <PaginaInicial />
      ) : (
        <LoginRegistrationPage onLogin={onLogin} />
      )}
    </div>
  );
};

export default App;
