// ClienteConsumoValor.tsx
import React, { useState, useEffect } from "react";
import { Button } from "react-bootstrap";  // Para usar os botões do React Bootstrap

const ClienteConsumoValor = () => {
    const [dadosCliente, setDadosCliente] = useState<any[]>([]);
    const [editando, setEditando] = useState<boolean>(false);
    const [clienteEditado, setClienteEditado] = useState<any | null>(null);

    useEffect(() => {
        // Dados simulados ou uma chamada à API
        setDadosCliente([
            { id: 1, nome: "Cliente A", consumoValor: 500 },
            { id: 2, nome: "Cliente B", consumoValor: 1200 },
            { id: 3, nome: "Cliente C", consumoValor: 800 },
        ]);
    }, []);

    const handleRegistro = () => {
        // Exemplo de adição de cliente
        const novoCliente = { id: dadosCliente.length + 1, nome: "Novo Cliente", consumoValor: 100 };
        setDadosCliente([...dadosCliente, novoCliente]);
    };

    const handleEdição = (cliente: any) => {
        setEditando(true);
        setClienteEditado(cliente);
    };

    const handleDelecao = (id: number) => {
        setDadosCliente(dadosCliente.filter(cliente => cliente.id !== id));
    };

    const handleSalvarEdicao = () => {
        setDadosCliente(dadosCliente.map(cliente =>
            cliente.id === clienteEditado.id ? clienteEditado : cliente
        ));
        setEditando(false);
        setClienteEditado(null);
    };

    return (
        <div>
            <h3>Clientes por Consumo de Valor</h3>
            <Button variant="primary" onClick={handleRegistro}>Registrar Cliente</Button>
            <ul>
                {dadosCliente.map(cliente => (
                    <li key={cliente.id}>
                        {editando && cliente.id === clienteEditado?.id ? (
                            <div>
                                <input
                                    type="text"
                                    value={clienteEditado?.nome}
                                    onChange={(e) => setClienteEditado({ ...clienteEditado, nome: e.target.value })}
                                />
                                <input
                                    type="number"
                                    value={clienteEditado?.consumoValor}
                                    onChange={(e) => setClienteEditado({ ...clienteEditado, consumoValor: +e.target.value })}
                                />
                                <Button variant="success" onClick={handleSalvarEdicao}>Salvar</Button>
                            </div>
                        ) : (
                            <div>
                                {cliente.nome} - R$ {cliente.consumoValor}
                                <Button variant="info" onClick={() => handleEdição(cliente)}>Editar</Button>
                                <Button variant="danger" onClick={() => handleDelecao(cliente.id)}>Deletar</Button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ClienteConsumoValor;
