import { useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Table = ({ data, headers, actions }: { data: any[], headers: string[], actions: boolean }) => (
  <table className="responsive-table centered">
    <thead>
      <tr>
        {headers.map((header, index) => <th key={index}>{header}</th>)}
      </tr>
    </thead>
    <tbody>
      {data.map((item) => (
        <tr key={item.id}>
          {Object.values(item).map((value, index) => <td key={index}>{value}</td>)}
          {actions && (
            <td>
              <button type="button" className="btn black white-text">Editar</button>
              <button type="button" className="btn black white-text">Deletar</button>
            </td>
          )}
        </tr>
      ))}
    </tbody>
  </table>
);

const Listagens = () => {
  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  const clienteData = [
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150 },
    // Mais dados...
  ];

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <Table
              data={clienteData.filter(cliente => cliente.valorConsumido > 300)}
              headers={["Id", "Nome", "CPF", "Telefone", "Valor Consumido", "Ações"]}
              actions={true}
            />
            <div className="center">
              <button className="btn green">Registrar</button>
            </div>
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            <Table
              data={clienteData.sort((a, b) => b.valorConsumido - a.valorConsumido)}
              headers={["Id", "Nome", "CPF", "Telefone", "Quantidade Consumida"]}
              actions={false}
            />
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            <Table
              data={clienteData.filter(cliente => cliente.valorConsumido < 200)}
              headers={["Id", "Nome", "CPF", "Telefone", "Valor Consumido"]}
              actions={false}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
