import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

interface Cliente {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  valorConsumido: number;
}

const Listagens = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedClient, setSelectedClient] = useState<Cliente | null>(null);
  const [editFormData, setEditFormData] = useState<Cliente>({
    id: 0,
    nome: "",
    cpf: "",
    telefone: "",
    valorConsumido: 0
  });

  useEffect(() => {
    const modals = document.querySelectorAll('.modal');
    M.Modal.init(modals);

    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);

    const tabs = document.querySelectorAll('.tabs');
    M.Tabs.init(tabs);
  }, []);

  const handleEdit = (cliente: Cliente) => {
    setSelectedClient(cliente);
    setEditFormData(cliente);
    const modalInstance = M.Modal.getInstance(document.getElementById('editModal')!);
    modalInstance.open();
  };

  const handleDelete = (id: number) => {
