import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

// Definindo o tipo de consumo
type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

// Definindo as propriedades esperadas pelo componente
type Props = {
  adicionarConsumo: (consumo: Consumo) => void; // Função passada como prop para adicionar consumo
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

class FormularioCadastroConsumo extends Component<Props> {
  componentDidMount() {
    // Inicializando o select do Materialize
    document.addEventListener("DOMContentLoaded", function () {
      var elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Coletando os dados do formulário
    const cliente = (document.getElementById("cliente") as HTMLSelectElement).value;
    const servico = (document.getElementById("servico") as HTMLSelectElement).value;
    const produto = (document.getElementById("produto") as HTMLSelectElement).value;
    const precoServico = (document.getElementById("precoServico") as HTMLInputElement).value;

    // Criando o objeto consumo
    const consumo: Consumo = {
      idPedido: `PED${Math.floor(Math.random() * 1000)}`,
      idProduto: produto,
      nomeProduto: `Produto ${produto}`,
      valor: precoServico,
    };

    // Passando o consumo para o componente pai (App)
    this.props.adicionarConsumo(consumo);
  };

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">Serviços</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </nav>

        <br />
        <div className="container">
          <h4 className="center-align">Cadastrar Consumo</h4>
          <form onSubmit={this.handleSubmit} className="col s12 formCli">
            <div className="row">
              <div className="input-field col s12 opcoes">
                <select id="cliente">
                  <option value="0">Selecione o Cliente</option>
                  <option value="1">Cliente - João Silva</option>
                  <option value="2">Cliente - Maria Souza</option>
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select id="servico">
                  <option value="1">Selecione o Serviço</option>
                  <option value="1">Limpeza de Pele</option>
                  <option value="2">Hidratação Facial</option>
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select id="produto">
                  <option value="2">Selecione o Produto</option>
                  <option value="1">Shampoo Hidratante</option>
                  <option value="2">Creme de Hidratação Intensiva</option>
                </select>
              </div>
            </div>
            <div className="input-field col s12 opcoes">
              <input
                type="number"
                id="precoServico"
                name="precoServico"
                step="0.01"
                min="0"
                required
              />
              <label htmlFor="precoServico">Preço do Serviço (R$)</label>
            </div>
            <div className="row">
              <div className="col s12">
                <button style={botaoStyle} type="submit">
                  Registrar
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

// Exportando o componente corretamente
export default FormularioCadastroConsumo;
