import { Component } from "react";
import CadastroCliente from "./CadastroCliente";
import ListaClientes from "./ListaClientes";

export default class Cliente extends Component {
  state = {
    mostrarCadastro: false,
  };

  alternarVisao = () => {
    this.setState((prevState) => ({ mostrarCadastro: !prevState.mostrarCadastro }));
  };

  render() {
    const { mostrarCadastro } = this.state;

    return (
      <div>
        <button onClick={this.alternarVisao}>
          {mostrarCadastro ? "Ver Lista de Clientes" : "Cadastrar Novo Cliente"}
        </button>
        {mostrarCadastro ? <CadastroCliente /> : <ListaClientes />}
      </div>
    );
  }
}
